CREATE OR REPLACE PROCEDURE gerar_setor_cargo ( cd_cargo_p bigint, cd_cargo_destino_p bigint, nm_usuario_p text) AS $body$
BEGIN
if (cd_cargo_p > 0) and (cd_cargo_destino_p > 0) then
	begin
	insert into cargo_setor(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		cd_cargo,
		cd_setor_atendimento)
	SELECT 	nextval('cargo_setor_seq'),
		clock_timestamp(),
		nm_usuario_p,
		cd_cargo_destino_p,
		a.cd_setor_atendimento
	from	cargo_setor a
	where	a.cd_cargo = cd_cargo_p
	and	not exists (	SELECT  1
				from	cargo_setor x
				where 	x.cd_cargo = cd_cargo_destino_p
				and	x.cd_setor_atendimento = a.cd_setor_atendimento);
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
