SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_sigiss_tipo3 ( dt_inicio_p timestamp, dt_fim_p timestamp, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_registro_w		varchar(1);
nr_linhas_detalhe_w	varchar(10);
vl_total_nota_w		varchar(15);
vl_bc_iss_w		varchar(15);
cd_estabelecimento_w	smallint;
ds_arquivo_ww		varchar(2000);
				

BEGIN 
 
select 	lpad(to_char(count(*)),'10','0') 
into STRICT	nr_linhas_detalhe_w 
from  nota_fiscal n, 
	nota_fiscal_item i, 
	operacao_nota o 
where  i.nr_sequencia = n.nr_sequencia 
and	o.cd_operacao_nf = n.cd_operacao_nf 
and	n.cd_estabelecimento = cd_estabelecimento_p 
and	n.dt_emissao between dt_inicio_p and fim_dia(dt_fim_p);
 
select	'9'											cd_registro, 
	lpad(coalesce(elimina_caractere_especial(campo_mascara_virgula(sum(n.vl_total_nota))),' '),'15','0') 	vl_total_nota, 
    	lpad(coalesce(elimina_caractere_especial(campo_mascara_virgula(sum(obter_valor_tipo_tributo_nota(n.nr_sequencia, 'B', 'ISS')))),' '),'15','0') vl_bc_iss, 
	n.cd_estabelecimento									cd_estabelecimento 
into STRICT	cd_registro_w, 
	vl_total_nota_w, 
	vl_bc_iss_w, 
	cd_estabelecimento_w 
from  nota_fiscal n, 
	nota_fiscal_item i, 
	operacao_nota o 
where  i.nr_sequencia = n.nr_sequencia 
and	o.cd_operacao_nf = n.cd_operacao_nf 
and	n.cd_estabelecimento = cd_estabelecimento_p 
and	n.dt_emissao between dt_inicio_p and fim_dia(dt_fim_p) 
group by n.cd_estabelecimento;
 
 
ds_arquivo_ww := cd_registro_w		|| 
		nr_linhas_detalhe_w	|| 
		vl_total_nota_w		|| 
		vl_bc_iss_w;
 
insert into w_inss_direp_arquivo(nr_sequencia, 
				 cd_estabelecimento, 
				 dt_atualizacao, 
				 nm_usuario, 
				 dt_atualizacao_nrec, 
				 nm_usuario_nrec, 
				 ds_arquivo_w) 
			values (nextval('w_inss_direp_arquivo_seq'), 
				 cd_estabelecimento_w, 
				 clock_timestamp(), 
				 nm_usuario_p, 
				 clock_timestamp(), 
				 nm_usuario_p, 
				 ds_arquivo_ww );
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
