CREATE OR REPLACE PROCEDURE gerar_sms_atend_unid_regra (nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text) AS $body$
DECLARE

 
qt_idade_w 				bigint;
ie_sexo_w				varchar(5);
cd_medico_resp_w		varchar(10);
nr_seq_evento_w			bigint;
ie_carater_inter_sus_w	varchar(10);
ie_tipo_atendimento_w	varchar(10);
cd_procedencia_w		bigint;
nr_seq_classificacao_w	bigint;
ie_clinica_w			integer;
nr_seq_classif_pac_w	bigint;

C01 CURSOR FOR 
	SELECT	a.nr_seq_evento 
	FROM regra_envio_sms a
LEFT OUTER JOIN regra_envio_sms_atend b ON (a.nr_sequencia = b.nr_seq_regra)
WHERE a.cd_estabelecimento	= cd_estabelecimento_p and a.ie_evento_disp = 'AISE' and qt_idade_w between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,9999) and coalesce(a.ie_sexo,ie_sexo_w) = ie_sexo_w and coalesce(a.cd_medico,coalesce(cd_medico_resp_w,'0')) = coalesce(cd_medico_resp_w,'0') and coalesce(b.ie_carater_inter_sus,coalesce(ie_carater_inter_sus_w,0)) = coalesce(ie_carater_inter_sus_w,'0') and coalesce(b.ie_tipo_atendimento,coalesce(ie_tipo_atendimento_w,0)) = coalesce(ie_tipo_atendimento_w,'0') and coalesce(b.ie_clinica,coalesce(ie_clinica_w,0)) = coalesce(ie_clinica_w,'0') and coalesce(a.cd_procedencia,coalesce(cd_procedencia_w,0)) = coalesce(cd_procedencia_w,'0') and coalesce(b.nr_seq_classificacao,coalesce(nr_seq_classificacao_w,0)) = coalesce(nr_seq_classificacao_w,'0') AND obter_se_sms_setor_alerta(nr_atendimento_p, a.nr_sequencia) = 'S' and (((a.nr_seq_classif IS NOT NULL AND a.nr_seq_classif::text <> '') and (coalesce(a.nr_seq_classif, nr_seq_classif_pac_w) = nr_seq_classif_pac_w)) or 
			((coalesce(a.nr_seq_classif::text, '') = '') and (obter_classif_regra_pf(a.nr_sequencia,coalesce(nr_seq_classif_pac_w,0)) = 'S'))) and coalesce(a.ie_situacao,'A') = 'A';						
 

BEGIN 
 
select	coalesce(max(obter_idade_pf(cd_pessoa_fisica,clock_timestamp(),'A')),0), 
	max(Obter_Sexo_PF(cd_pessoa_fisica,'C')), 
	max(cd_medico_resp), 
	max(ie_carater_inter_sus), 
	max(ie_tipo_atendimento), 
	max(cd_procedencia), 
	max(nr_seq_classificacao), 
	max(ie_clinica) 
into STRICT	qt_idade_w, 
	ie_sexo_w, 
	cd_medico_resp_w, 
	ie_carater_inter_sus_w, 
	ie_tipo_atendimento_w, 
	cd_procedencia_w, 
	nr_seq_classificacao_w, 
	ie_clinica_w 
from 	atendimento_paciente 
where	nr_atendimento = nr_atendimento_p;
 
nr_seq_classif_pac_w := obter_classificacao_pf(cd_pessoa_fisica_p);
 
open C01;
loop 
fetch C01 into	 
	nr_seq_evento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL gerar_evento_paciente(nr_seq_evento_w,nr_atendimento_p,cd_pessoa_fisica_p,null,nm_usuario_p,null);	
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
