CREATE OR REPLACE PROCEDURE gerar_solicitacao_itens_pend ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_solicitante_p text, cd_material_p bigint, cd_unidade_medida_compra_p text, qt_material_p bigint, nr_solic_compra_p INOUT bigint, cd_local_estoque_p bigint, nr_ordem_compra_p bigint default null, nr_item_oci_p bigint default null) AS $body$
DECLARE


nr_solic_compra_w		bigint;
nr_item_solic_w		integer := 0;

BEGIN

nr_solic_compra_w := nr_solic_compra_p;

if (nr_solic_compra_w = 0) then
	begin
	select  nextval('solic_compra_seq')
	into STRICT	nr_solic_compra_w
	;
	
	insert into solic_compra(
		nr_solic_compra,
		cd_pessoa_solicitante,
		dt_solicitacao_compra,
		cd_estabelecimento,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		ie_aviso_aprov_oc,
		ie_urgente,
		cd_local_estoque,
		ie_tipo_solicitacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec)
	values (nr_solic_compra_w,
		cd_pessoa_solicitante_p,
		clock_timestamp(),
		cd_estabelecimento_p,
		clock_timestamp(),
		nm_usuario_p,
		'A',
		'N',
		'N',
		cd_local_estoque_p,
		6,
		nm_usuario_p,
		clock_timestamp());
	end;
end if;


select  coalesce(max(nr_item_solic_compra),0) + 1
into STRICT 	nr_item_solic_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_w;


insert into solic_compra_item(
	nr_solic_compra,
	cd_material,
	cd_unidade_medida_compra,
	qt_material,
	nr_item_solic_compra,
	dt_atualizacao,
	nm_usuario,
	ie_situacao,
	ie_geracao,
	nr_ordem_compra_orig,
	nr_item_oci_orig,
	qt_saldo_disp_estoque)
values (nr_solic_compra_w,
	cd_material_p ,
	cd_unidade_medida_compra_p,
	qt_material_p,
	nr_item_solic_w,
	clock_timestamp(),
	nm_usuario_p,
	'A',
	'S',
	nr_ordem_compra_p,
	nr_item_oci_p,
	obter_saldo_estoque_nuvem(cd_estabelecimento_p, cd_material_p, cd_local_estoque_p, trunc(clock_timestamp(),'mm')));

insert	into solic_compra_item_entrega( 	
	nr_solic_compra,
	nr_item_solic_compra,
	qt_entrega_solicitada,
	nr_item_solic_compra_entr,
	dt_entrega_solicitada,
	dt_atualizacao,
	nm_usuario)
values (	nr_solic_compra_w,
	nr_item_solic_w,
	qt_material_p,
	nr_item_solic_w,
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p);

commit;
nr_solic_compra_p := nr_solic_compra_w;
end;
