CREATE OR REPLACE PROCEDURE gerar_solic_compra_ges_opme ( nr_seq_protocolo_p bigint, cd_pessoa_fisica_solic_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_solic_compra_p INOUT bigint) AS $body$
DECLARE

 
cd_material_w			bigint;
qt_prevista_w			double precision;	
qt_dias_entrega_padrao_w	bigint := 0;
dt_entrega_solic_w		timestamp;
nm_paciente_lote_w		varchar(255);

C01 CURSOR FOR 
SELECT	 	a.cd_material, 
		CEIL(SUM(coalesce(a.qt_prevista,0))) qt_prevista, 
		SUBSTR(obter_nome_paciente_lote_opme(a.nr_lote_producao,'D'),1,255) nm_paciente_lote 
FROM  		lote_producao_comp a 
WHERE 		a.nr_seq_protocolo = nr_seq_protocolo_p 
GROUP BY 	SUBSTR(obter_nome_paciente_lote_opme(a.nr_lote_producao,'D'),1,255), 
		a.cd_material, 
		SUBSTR(obter_dados_material(cd_material,'UMC'),1,5), 
		SUBSTR(obter_desc_material(a.CD_MATERIAL),1,60) 
ORDER BY 1;


BEGIN	 
begin 
 
	select	coalesce(qt_dias_entrega_padrao,2) 
	into STRICT	qt_dias_entrega_padrao_w 
	from	parametro_compras 
	where	cd_estabelecimento = cd_estabelecimento_p;
	 
	dt_entrega_solic_w := trunc(clock_timestamp(),'dd') + qt_dias_entrega_padrao_w;
 
	select	nextval('solic_compra_seq') 
	into STRICT	nr_solic_compra_p 
	;
	 
	insert into solic_compra( 
		nr_solic_compra, 
		cd_estabelecimento, 
		dt_solicitacao_compra, 
		dt_atualizacao, 
		nm_usuario, 
		ie_situacao, 
		cd_pessoa_solicitante, 
		ds_observacao, 
		ie_aviso_chegada, 
		ie_aviso_aprov_oc, 
		ie_urgente, 
		ie_tipo_solicitacao, 
		ie_comodato, 
		ie_semanal, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec) 
	values (	nr_solic_compra_p, 
		cd_estabelecimento_p, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		'A', 
		cd_pessoa_fisica_solic_p, 
		'', 
		'N', 
		'N', 
		'N', 
		'7', 
		'N', 
		'N', 
		nm_usuario_p, 
		clock_timestamp());
 
	update protocolo_fornec_opme a 
	set  a.nr_solic_compra 	= nr_solic_compra_p 
	where a.nr_sequencia 		= nr_seq_protocolo_p;
	 
	commit;
	 
	open C01;
	loop 
	fetch C01 into	 
		cd_material_w, 
		qt_prevista_w, 
		nm_paciente_lote_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL Gerar_Solic_Compra_Item(nr_solic_compra_p,cd_material_w,'C',qt_prevista_w,dt_entrega_solic_w,nm_usuario_p,'N',0,nm_paciente_lote_w);	
		 
		end;
	end loop;
	close C01;	
commit;
 
exception 
when others then 
	null;
end;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_solic_compra_ges_opme ( nr_seq_protocolo_p bigint, cd_pessoa_fisica_solic_p text, cd_estabelecimento_p bigint, nm_usuario_p text, nr_solic_compra_p INOUT bigint) FROM PUBLIC;

