CREATE OR REPLACE PROCEDURE gerar_solic_compra ( cd_estabelecimento_p bigint, ie_aviso_chegada_p text, ie_aviso_aprov_oc_p text, cd_local_estoque_p bigint, nm_usuario_p text, nr_solic_compra_p INOUT bigint) AS $body$
DECLARE

 
cd_estabelecimento_w		smallint;
qt_dias_entrega_padrao_w	bigint := 0;
cd_pessoa_fisica_w		varchar(10) := '';
dt_entrega_solic_w		timestamp;
nr_solic_compra_w		bigint;
cd_setor_atendimento_w		integer;
cd_local_estoque_w		local_estoque.cd_local_estoque%type;
ie_regra_local_estoque_w	varchar(1);


BEGIN 
cd_estabelecimento_w		:= cd_estabelecimento_p;
cd_local_estoque_w		:= cd_local_estoque_p;
 
if (cd_estabelecimento_w = 0) then 
	select	min(cd_estabelecimento) 
	into STRICT	cd_estabelecimento_w 
	from	parametro_compras;
end if;
 
if (cd_local_estoque_w > 0) then 
 
	select	coalesce(obter_regra_local_solic(cd_estabelecimento_w,cd_local_estoque_w),'S') 
	into STRICT	ie_regra_local_estoque_w 
	;
	 
	if (ie_regra_local_estoque_w = 'N') then 
		cd_local_estoque_w := null;	
	end if;
end if;
 
 
select	coalesce(qt_dias_entrega_padrao,2) 
into STRICT	qt_dias_entrega_padrao_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
select	cd_pessoa_fisica 
into STRICT	cd_pessoa_fisica_w 
from	usuario 
where	nm_usuario = nm_usuario_p;
 
dt_entrega_solic_w := trunc(clock_timestamp(),'dd') + qt_dias_entrega_padrao_w;
 
select 	obter_setor_ativo 
into STRICT	cd_setor_atendimento_w
;
 
select	nextval('solic_compra_seq') 
into STRICT	nr_solic_compra_w
;
 
begin 
insert into solic_compra( 
	nr_solic_compra, 
	cd_estabelecimento, 
	dt_solicitacao_compra, 
	dt_atualizacao, 
	nm_usuario, 
	ie_situacao, 
	cd_pessoa_solicitante, 
	cd_local_estoque, 
	cd_centro_custo, 
	cd_conta_contabil, 
	cd_setor_atendimento, 
	ds_observacao, 
	cd_motivo_baixa, 
	dt_baixa, 
	dt_liberacao, 
	cd_pessoa_autoriza, 
	dt_autorizacao, 
	dt_impressao, 
	ie_aviso_chegada, 
	ie_aviso_aprov_oc, 
	ie_urgente, 
	ie_tipo_solicitacao, 
	ie_comodato, 
	ie_semanal, 
	nm_usuario_nrec, 
	dt_atualizacao_nrec) 
values (	nr_solic_compra_w, 
	cd_estabelecimento_w, 
	clock_timestamp(), 
	clock_timestamp(), 
	nm_usuario_p, 
	'A', 
	cd_pessoa_fisica_w, 
	cd_local_estoque_w, 
	null, 
	null, 
	cd_setor_atendimento_w, 
	null, 
	null, 
	null, 
	null, 
	cd_pessoa_fisica_w, 
	null, 
	null, 
	ie_aviso_chegada_p, 
	ie_aviso_aprov_oc_p, 
	'N', 
	1, 
	'N', 
	'N', 
	nm_usuario_p, 
	clock_timestamp());
exception 
	when others then 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266029);
		--'Erro ao gravar solicitacao' 
end;
 
COMMIT;
 
nr_solic_compra_p		:= nr_solic_compra_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
