CREATE OR REPLACE PROCEDURE gerar_solic_kit_mat_comp (nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w		integer;
nr_seq_item_w		integer;
qt_solicitada_w		double precision;
qt_material_w		double precision;
qt_disponivel_w		double precision;
qt_kit_solic_w		double precision;
cd_kit_material_w	integer;
cd_local_estoque_w	smallint;

C01 CURSOR FOR
SELECT	a.cd_material,
	a.qt_material,
	a.nr_sequencia
from	componente_kit a
where	cd_kit_material = cd_kit_material_w
and	ie_situacao = 'A'
and	((coalesce(cd_estab_regra::text, '') = '') or (cd_estab_regra = cd_estabelecimento_p))
and	not exists (	select	1
			from	solic_kit_mat_comp x
			where	x.nr_seq_solic_kit = nr_sequencia_p
			and	x.nr_seq_item_kit = a.nr_sequencia);


BEGIN

select	qt_solicitada,
	cd_kit_material,
	cd_local_estoque
into STRICT	qt_kit_solic_w,
	cd_kit_material_w,
	cd_local_estoque_w
from	solic_kit_material
where	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_material_w,
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_solicitada_w := qt_material_w * qt_kit_solic_w;

	qt_disponivel_w := null;

	insert into solic_kit_mat_comp(
		nr_sequencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_solic_kit,
		nr_seq_item_kit,
		cd_material,
		qt_solicitada,
		qt_disponivel)
	values (	nextval('solic_kit_mat_comp_seq'),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_sequencia_p,
		nr_seq_item_w,
		cd_material_w,
		qt_solicitada_w,
		qt_disponivel_w);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
