CREATE OR REPLACE PROCEDURE gerar_sql_patient_deletion (nr_seq_deletion_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_tabela_w		patient_deletion_table.nm_tabela%type;
ds_sql_w		patient_deletion_table.ds_sql_ligacao%type;


BEGIN

	select	max(nm_tabela)
	into STRICT	nm_tabela_w
	from	patient_deletion_table
	where	nr_sequencia = nr_seq_deletion_p;

	if (nm_tabela_w IS NOT NULL AND nm_tabela_w::text <> '') then
		select	obter_ligacao_pf_tabela(nm_tabela_w)
		into STRICT	ds_sql_w
		;

		if (ds_sql_w IS NOT NULL AND ds_sql_w::text <> '') then
			update	patient_deletion_table
			set	ds_sql_ligacao = ds_sql_w,
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_sequencia = nr_seq_deletion_p;

			commit;
		else
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1026338);
		end if;
	end if;

end;
$body$
