CREATE OR REPLACE PROCEDURE gerar_status_obrigatorios (nr_sequencia_regra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequence_w	bigint;
sequence_est_w  bigint;


BEGIN

select nextval('status_regra_entrega_seq')
into STRICT nr_sequence_w
;

nr_sequence_w := nr_sequence_w-1;

	insert into STATUS_REGRA_ENTREGA(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec ,nm_usuario_nrec,ie_status_execucao,nr_seq_regra) values (nr_sequence_w,clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p ,'40', nr_sequencia_regra_p);

nr_sequence_w := nr_sequence_w+1;

	insert into STATUS_REGRA_ENTREGA(nr_sequencia, dt_atualizacao, nm_usuario, dt_atualizacao_nrec ,nm_usuario_nrec,ie_status_execucao,nr_seq_regra) values (nr_sequence_w,clock_timestamp(), nm_usuario_p, clock_timestamp(), nm_usuario_p ,'60', nr_sequencia_regra_p);

	select nextval('estagio_status_entrega_seq')
	into STRICT sequence_est_w
	;

	insert into Estagio_Status_entrega(NR_SEQUENCIA, DT_ATUALIZACAO, NM_USUARIO,DT_ATUALIZACAO_NREC, NM_USUARIO_NREC, NR_SEQ_ESTAGIO,NR_SEQ_STATUS)  values (
	sequence_est_w, clock_timestamp(), nm_usuario_p, clock_timestamp(),nm_usuario_p,(SELECT nr_sequencia from estagio_entrega where ds_estagio = wheb_mensagem_pck.get_texto(717235)),
	nr_sequence_w);

end;
