CREATE OR REPLACE PROCEDURE gerar_substituicao_proced ( nr_sequencia_propaci_p bigint, nr_sequencia_p bigint, nr_sequencia_auditoria_p bigint, cd_procedimento_substituto_p text, ie_origem_proced_p bigint, cd_motivo_audit_p text, nm_usuario_p text, nr_seq_proc_interno_p bigint, nr_seq_exame_p bigint) AS $body$
DECLARE

 
nr_seq_gerada_w				bigint;
nr_seq_motivo_w				bigint;
nr_interno_conta_w			bigint;
qt_procedimento_w			bigint;
cd_convenio_w				bigint;
					

BEGIN 
 
select	nr_interno_conta, 
	qt_procedimento, 
	cd_convenio 
into STRICT	nr_interno_conta_w, 
	qt_procedimento_w, 
	cd_convenio_w 
from 	procedimento_paciente 
where	nr_sequencia = nr_sequencia_propaci_p;	
 
if (nr_sequencia_propaci_p IS NOT NULL AND nr_sequencia_propaci_p::text <> '') then 
 
	update	auditoria_propaci 
	set	qt_ajuste = 0, 
		nm_usuario = nm_usuario_p, 
		dt_atualizacao = clock_timestamp(), 
		nr_seq_motivo = cd_motivo_audit_p 
	where	nr_sequencia = nr_sequencia_p;
	 
	nr_seq_gerada_w := duplicar_proc_paciente(nr_sequencia_propaci_p, nm_usuario_p, nr_seq_gerada_w);
	 
	update procedimento_paciente 
	set 	cd_procedimento 	= cd_procedimento_substituto_p, 
		ie_origem_proced	= ie_origem_proced_p, 
		nr_seq_proc_interno	= CASE WHEN coalesce(nr_seq_proc_interno_p,0)=0 THEN null  ELSE nr_seq_proc_interno_p END , 
		nr_seq_exame		= CASE WHEN coalesce(nr_seq_exame_p,0)=0 THEN null  ELSE nr_seq_exame_p END , 
		ie_auditoria = 'N' 
	where nr_sequencia = nr_seq_gerada_w;
	 
	CALL atualiza_preco_procedimento(nr_seq_gerada_w, cd_convenio_w, nm_usuario_p);
	 
	update	procedimento_paciente 
	set	ie_auditoria = 'S' 
	where	nr_sequencia = nr_seq_gerada_w;
	 
	CALL Atualizar_Lista_Itens_Audit(nr_interno_conta_w, nr_seq_gerada_w, 2, qt_procedimento_w, nm_usuario_p, 
						cd_motivo_audit_p, nr_sequencia_auditoria_p, 'N');
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
