CREATE OR REPLACE PROCEDURE gerar_subtipo_protocolo_prescr ( cd_protocolo_p bigint, nm_subtipo_Prot_p text, nm_usuario_p text, cd_subtipo_Prot_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_interna_w 	bigint;

BEGIN


select      coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from	protocolo_medicacao
where	coalesce(cd_protocolo,cd_protocolo_p) = cd_protocolo_p;


if (nr_sequencia_w > 0) and (cd_protocolo_p > 0) and (nm_subtipo_Prot_p IS NOT NULL AND nm_subtipo_Prot_p::text <> '') then

	insert into protocolo_medicacao(
					nr_seq_interna,
					cd_protocolo,
					ie_situacao,
					nm_medicacao,
					nr_ciclos,
					nr_sequencia)
				values (
					nextval('protocolo_medicacao_seq'),
					cd_protocolo_p,
					'A',
					substr(nm_subtipo_Prot_p,1,255),
					1,
					nr_sequencia_w);
	commit;
                if (nr_sequencia_w > 0) then
	  	cd_subtipo_Prot_p	:= nr_sequencia_w;
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
