CREATE OR REPLACE PROCEDURE gerar_suspensao_itens_sae ( nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


					
nr_seq_pe_prescr_w		bigint;


c01 CURSOR FOR
	SELECT	a.nr_sequencia	
	from	pe_prescricao a
	where	a.nr_atendimento = nr_atendimento_p
	and		a.dt_validade_prescr > clock_timestamp()
	/*and 	not exists  ( select		1 
			       from		procedimento_paciente c
			       where		c.nr_sequencia = (	select	max(d.nr_sequencia)
									from	procedimento_paciente d
									where	c.nr_sequencia_prescricao = d.nr_sequencia_prescricao
									and		c.nr_prescricao = d.nr_prescricao)
						and			c.dt_conta is not null
						and			c.nr_sequencia_prescricao = a.nr_sequencia
						and			c.nr_prescricao = a.nr_prescricao)*/
	
	and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');


BEGIN

open C01;
loop
fetch C01 into	
	nr_seq_pe_prescr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	CALL Suspender_prescricao_SAE( nr_seq_pe_prescr_w, null, obter_desc_expressao(318922), nm_usuario_p);
end loop;
close C01;
		
if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
