CREATE OR REPLACE PROCEDURE gerar_tabela_atributo_idioma (cd_funcao_p bigint, nr_seq_idioma_p bigint, nm_usuario_p text) AS $body$
DECLARE


    nm_tabela_w            varchar(50);
    nm_atributo_w          varchar(50);
    nm_atributo_traducao_w varchar(50);
    nr_seq_visao_w         bigint;
    ds_texto_w             varchar(4000);
    cd_funcao_w            integer;
    nr_seq_traducao_w      bigint;

    c01 CURSOR FOR
        SELECT a.nm_tabela,
               a.nm_atributo,
               'DS_LABEL',
               a.ds_label,
               b.cd_funcao
          from tabela_sistema  b,
               tabela_atributo a
         where b.nm_tabela = a.nm_tabela
           and b.cd_funcao = cd_funcao_p
           and (a.nr_seq_apresent IS NOT NULL AND a.nr_seq_apresent::text <> '')
           and (a.ds_label IS NOT NULL AND a.ds_label::text <> '')
           and not exists (SELECT 1
                  from tab_atributo_idioma x
                 where x.nm_tabela = a.nm_tabela
                   and x.nm_atributo = a.nm_atributo
                   and x.nm_atributo_traducao = 'DS_LABEL'
                   and coalesce(x.nr_seq_visao::text, '') = ''
                   and x.nr_seq_idioma = nr_seq_idioma_p)

union

        select a.nm_tabela,
               a.nm_atributo,
               'DS_LABEL_GRID',
               a.ds_label_grid,
               b.cd_funcao
          from tabela_sistema  b,
               tabela_atributo a
         where b.nm_tabela = a.nm_tabela
           and b.cd_funcao = cd_funcao_p
           and (a.nr_seq_apresent IS NOT NULL AND a.nr_seq_apresent::text <> '')
           and (a.ds_label_grid IS NOT NULL AND a.ds_label_grid::text <> '')
           and not exists (select 1
                  from tab_atributo_idioma x
                 where x.nm_tabela = a.nm_tabela
                   and x.nm_atributo = a.nm_atributo
                   and x.nm_atributo_traducao = 'DS_LABEL_GRID'
                   and coalesce(x.nr_seq_visao::text, '') = ''
                   and x.nr_seq_idioma = nr_seq_idioma_p)
        
union

        select a.nm_tabela,
               a.nm_atributo,
               'DS_ATRIBUTO',
               a.ds_atributo,
               b.cd_funcao
          from tabela_sistema  b,
               tabela_atributo a
         where b.nm_tabela = a.nm_tabela
           and b.cd_funcao = cd_funcao_p
           and (a.nr_seq_apresent IS NOT NULL AND a.nr_seq_apresent::text <> '')
           and (a.ds_atributo IS NOT NULL AND a.ds_atributo::text <> '')
           and not exists (select 1
                  from tab_atributo_idioma x
                 where x.nm_tabela = a.nm_tabela
                   and x.nm_atributo = a.nm_atributo
                   and x.nm_atributo_traducao = 'DS_ATRIBUTO'
                   and coalesce(x.nr_seq_visao::text, '') = ''
                   and x.nr_seq_idioma = nr_seq_idioma_p)
        
union

        select a.nm_tabela,
               a.nm_atributo,
               'DS_VALORES',
               a.ds_valores,
               b.cd_funcao
          from tabela_sistema  b,
               tabela_atributo a
         where b.nm_tabela = a.nm_tabela
           and b.cd_funcao = cd_funcao_p
           and (a.nr_seq_apresent IS NOT NULL AND a.nr_seq_apresent::text <> '')
           and (a.ds_valores IS NOT NULL AND a.ds_valores::text <> '')
           and a.ie_componente = 'rg'
           and not exists (select 1
                  from tab_atributo_idioma x
                 where x.nm_tabela = a.nm_tabela
                   and x.nm_atributo = a.nm_atributo
                   and x.nm_atributo_traducao = 'DS_VALORES'
                   and coalesce(x.nr_seq_visao::text, '') = ''
                   and x.nr_seq_idioma = nr_seq_idioma_p);

    c02 CURSOR FOR
        SELECT b.nm_tabela,
               a.nm_atributo,
               'DS_LABEL',
               a.nr_sequencia,
               a.ds_label,
               b.cd_funcao
          from tabela_visao          b,
               tabela_visao_atributo a
         where b.nr_sequencia = a.nr_sequencia
           and b.cd_funcao = cd_funcao_p
           and (a.nr_seq_apresent IS NOT NULL AND a.nr_seq_apresent::text <> '')
           and (a.ds_label IS NOT NULL AND a.ds_label::text <> '')
           and not exists (SELECT 1
                  from tab_atributo_idioma x
                 where x.nm_tabela = b.nm_tabela
                   and x.nm_atributo = a.nm_atributo
                   and x.nm_atributo_traducao = 'DS_LABEL'
                   and x.nr_seq_visao = a.nr_sequencia
                   and x.nr_seq_idioma = nr_seq_idioma_p)

union

        select b.nm_tabela,
               a.nm_atributo,
               'DS_LABEL_GRID',
               a.nr_sequencia,
               a.ds_label_grid,
               b.cd_funcao
          from tabela_visao          b,
               tabela_visao_atributo a
         where b.nr_sequencia = a.nr_sequencia
           and b.cd_funcao = cd_funcao_p
           and (a.nr_seq_apresent IS NOT NULL AND a.nr_seq_apresent::text <> '')
           and (a.ds_label_grid IS NOT NULL AND a.ds_label_grid::text <> '')
           and not exists (select 1
                  from tab_atributo_idioma x
                 where x.nm_tabela = b.nm_tabela
                   and x.nm_atributo = a.nm_atributo
                   and x.nm_atributo_traducao = 'DS_LABEL_GRID'
                   and x.nr_seq_visao = a.nr_sequencia
                   and x.nr_seq_idioma = nr_seq_idioma_p)
        
union

        select b.nm_tabela,
               a.nm_atributo,
               'DS_VALORES',
               a.nr_sequencia,
               a.ds_valores,
               b.cd_funcao
          from tabela_visao          b,
               tabela_visao_atributo a
         where b.nr_sequencia = a.nr_sequencia
           and b.cd_funcao = cd_funcao_p
           and (a.nr_seq_apresent IS NOT NULL AND a.nr_seq_apresent::text <> '')
           and (a.ds_valores IS NOT NULL AND a.ds_valores::text <> '')
           and a.ie_componente = 'rg'
           and not exists (select 1
                  from tab_atributo_idioma x
                 where x.nm_tabela = b.nm_tabela
                   and x.nm_atributo = a.nm_atributo
                   and x.nm_atributo_traducao = 'DS_VALORES'
                   and x.nr_seq_visao = a.nr_sequencia
                   and x.nr_seq_idioma = nr_seq_idioma_p);


BEGIN
    if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
        begin
            open c01;
            loop
                fetch c01
                    into nm_tabela_w,
                         nm_atributo_w,
                         nm_atributo_traducao_w,
                         ds_texto_w,
                         cd_funcao_w;
                EXIT WHEN NOT FOUND; /* apply on c01 */
                begin
                    if ((trim(both ds_texto_w) IS NOT NULL AND (trim(both ds_texto_w))::text <> '')) then
                        begin

                            $if $$tasy_local_dict=true $then
                            nr_seq_traducao_w := get_remote_sequence('seq:tab_atributo_idioma_seq');
                            $else
                            select nextval('tab_atributo_idioma_seq') into STRICT nr_seq_traducao_w;
                            $end

                            insert into tab_atributo_idioma(nr_sequencia,
                                 dt_atualizacao_nrec,
                                 nm_usuario_nrec,
                                 dt_atualizacao,
                                 nm_usuario,
                                 nm_tabela,
                                 nm_atributo,
                                 nm_atributo_traducao,
                                 nr_seq_visao,
                                 ds_descricao,
                                 cd_funcao,
                                 nr_seq_idioma,
                                 ds_traducao,
                                 ds_traducao_long,
                                 ie_necessita_revisao)
                            values (nr_seq_traducao_w,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 nm_tabela_w,
                                 nm_atributo_w,
                                 nm_atributo_traducao_w,
                                 null,
                                 ds_texto_w,
                                 cd_funcao_w,
                                 nr_seq_idioma_p,
                                 CASE WHEN nm_atributo_w='DS_ATRIBUTO' THEN  null  ELSE ' ' END ,
                                 CASE WHEN nm_atributo_w='DS_ATRIBUTO' THEN  ' '  ELSE null END ,
                                 'T');
                        end;
                    end if;
                end;
            end loop;
            close c01;

            open c02;
            loop
                fetch c02
                    into nm_tabela_w,
                         nm_atributo_w,
                         nm_atributo_traducao_w,
                         nr_seq_visao_w,
                         ds_texto_w,
                         cd_funcao_w;
                EXIT WHEN NOT FOUND; /* apply on c02 */
                begin
                    if ((trim(both ds_texto_w) IS NOT NULL AND (trim(both ds_texto_w))::text <> '')) then
                        begin

                            $if $$tasy_local_dict=true $then
                            nr_seq_traducao_w := get_remote_sequence('seq:tab_atributo_idioma_seq');
                            $else
                            select nextval('tab_atributo_idioma_seq') into STRICT nr_seq_traducao_w;
                            $end

                            insert into tab_atributo_idioma(nr_sequencia,
                                 dt_atualizacao_nrec,
                                 nm_usuario_nrec,
                                 dt_atualizacao,
                                 nm_usuario,
                                 nm_tabela,
                                 nm_atributo,
                                 nm_atributo_traducao,
                                 nr_seq_visao,
                                 ds_descricao,
                                 cd_funcao,
                                 nr_seq_idioma,
                                 ds_traducao,
                                 ds_traducao_long,
                                 ie_necessita_revisao)
                            values (nr_seq_traducao_w,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 nm_tabela_w,
                                 nm_atributo_w,
                                 nm_atributo_traducao_w,
                                 nr_seq_visao_w,
                                 ds_texto_w,
                                 cd_funcao_w,
                                 nr_seq_idioma_p,
                                 CASE WHEN nm_atributo_w='DS_ATRIBUTO' THEN  null  ELSE ' ' END ,
                                 CASE WHEN nm_atributo_w='DS_ATRIBUTO' THEN  ' '  ELSE null END ,
                                 'T');
                        end;
                    end if;
                end;
            end loop;
            close c02;
        end;
    end if;
    commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
