				and	cd_tipo_tabela_custo		= c01_w.cd_tipo_tabela_custo  LIMIT 1;

				exception when others then
					qt_tabela_w := 0;
				end;

				if (qt_tabela_w = 0) then
					begin
					-- Caso utilizar multi-estabelecimento e não for as tabelas do tipo 1, 3, 4, 8 ou 9 criar uma tabela sem estabelecimento informado, que será utilizada por todos
					ds_tabela_custo_w := substr(ds_tabela_custo_w || ' ' || wheb_mensagem_pck.get_texto(798617),1,100);

					select	coalesce(max(cd_tabela_custo),0)
					into STRICT	cd_tabela_custo_w
					from	tabela_custo
					where	coalesce(cd_empresa,cd_empresa_w) = cd_empresa_w;


					cd_tabela_custo_w	:= cd_tabela_custo_w + 1;

					select	nextval('tabela_custo_seq')
					into STRICT	nr_sequencia_w
					;

					insert into tabela_custo(
						nr_sequencia,
						cd_estabelecimento,
						cd_tabela_custo,
						ds_tabela_custo,
						nm_usuario,
						dt_atualizacao,
						cd_tipo_tabela_custo,
						ie_situacao_tabela,
						dt_vigencia_inicial,
						dt_vigencia_final,
						dt_inicio_uso,
						dt_final_uso,
						dt_mes_referencia,
						ie_orcado_real,
						cd_empresa,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						ie_situacao)
					values (	nr_sequencia_w,
						null,
						cd_tabela_custo_w,
						ds_tabela_custo_w,
						nm_usuario_p,
						clock_timestamp(),
						c01_w.cd_tipo_tabela_custo,
						'1',
						dt_mes_referencia_w,
						last_day(dt_mes_referencia_w),
						null,
						null,
						dt_mes_referencia_w,
						ie_orcado_real_p,
						cd_empresa_w,
						clock_timestamp(),
						nm_usuario_p,
						'A');

					begin
					insert into Tabela_parametro(
						cd_estabelecimento,
						cd_tabela_custo,
						nr_seq_parametro,
						nm_usuario,
						dt_atualizacao,
						cd_tabela_parametro,
						cd_comp_parametro,
						dt_parametro,
						qt_parametro,
						nr_seq_tabela,
						nr_seq_tabela_param,
						dt_atualizacao_nrec,
						nm_usuario_nrec)
					SELECT	cd_estabelecimento_p,
						cd_tabela_custo_w,
						nr_seq_parametro,
						nm_usuario_p,
						clock_timestamp(),
						null,
						null,
						null,
						null,
						nr_sequencia_w,
						null,
						clock_timestamp(),
						nm_usuario_p
					from	tipo_tabela_custo_param
					where	cd_tipo_tabela_custo	= c01_w.cd_tipo_tabela_custo;

					exception when others then
						CALL Wheb_mensagem_pck.exibir_mensagem_abort(212892);
					end;
					end;
				end if;
				end;
			end if;
		end;
	else

		begin
		select	holding_pck.get_grupo_emp_usuario(cd_empresa_w),
			holding_pck.get_nm_grupo_empresa(null, cd_empresa_w)
		into STRICT	nr_seq_grupo_emp_w,
			nm_grupo_empresa_w
		;

			begin
				select	1
				into STRICT	qt_tabela_w
				from	tabela_custo
				where	dt_mes_referencia		= dt_mes_referencia_w
				and	cd_empresa			= cd_empresa_w
				and	ie_orcado_real			= ie_orcado_real_p
				and	cd_tipo_tabela_custo		= c01_w.cd_tipo_tabela_custo  LIMIT 1;
			exception when others then
				qt_tabela_w := 0;
			end;

		if (qt_tabela_w = 0) then
			begin
			ds_tabela_custo_w := substr(ds_tabela_custo_w || ' ' || nm_grupo_empresa_w ,1,100);

			select	coalesce(max(cd_tabela_custo),0)
			into STRICT	cd_tabela_custo_w
			from	tabela_custo
			where	coalesce(cd_empresa,cd_empresa_w) = cd_empresa_w;


			cd_tabela_custo_w	:= cd_tabela_custo_w + 1;

			select	nextval('tabela_custo_seq')
			into STRICT	nr_sequencia_w
			;

			insert into tabela_custo(
				nr_sequencia,
				cd_estabelecimento,
				cd_tabela_custo,
				ds_tabela_custo,
				nm_usuario,
				dt_atualizacao,
				cd_tipo_tabela_custo,
				ie_situacao_tabela,
				dt_vigencia_inicial,
				dt_vigencia_final,
				dt_inicio_uso,
				dt_final_uso,
				dt_mes_referencia,
				ie_orcado_real,
				cd_empresa,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				ie_situacao,
				nr_seq_grupo_emp)
			values (	nr_sequencia_w,
				null,
				cd_tabela_custo_w,
				ds_tabela_custo_w,
				nm_usuario_p,
				clock_timestamp(),
				c01_w.cd_tipo_tabela_custo,
				'1',
				dt_mes_referencia_w,
				last_day(dt_mes_referencia_w),
				null,
				null,
				dt_mes_referencia_w,
				ie_orcado_real_p,
				cd_empresa_w,
				clock_timestamp(),
				nm_usuario_p,
				'A',
				nr_seq_grupo_emp_w);

			begin
			insert into Tabela_parametro(
				cd_estabelecimento,
				cd_tabela_custo,
				nr_seq_parametro,
				nm_usuario,
				dt_atualizacao,
				cd_tabela_parametro,
				cd_comp_parametro,
				dt_parametro,
				qt_parametro,
				nr_seq_tabela,
				nr_seq_tabela_param,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
			SELECT	cd_estabelecimento_p,
				cd_tabela_custo_w,
				nr_seq_parametro,
				nm_usuario_p,
				clock_timestamp(),
				null,
				null,
				null,
				null,
				nr_sequencia_w,
				null,
				clock_timestamp(),
				nm_usuario_p
			from	tipo_tabela_custo_param
			where	cd_tipo_tabela_custo	= c01_w.cd_tipo_tabela_custo;

			exception when others then
				CALL Wheb_mensagem_pck.exibir_mensagem_abort(212892);
			end;
			end;
		end if;
		end;

	end if;
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	c02_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin

	open c03;
	loop
	fetch c03 into
		c03_w;
	EXIT WHEN NOT FOUND; /* apply on c03 */
		null;

		update	tabela_parametro
		set	cd_tabela_parametro	= c03_w.cd_tabela_custo,
			nr_seq_tabela_param	= c03_w.nr_sequencia
		where	nr_seq_tabela		= c03_w.nr_seq_tabela
		and	nr_seq_parametro	= c03_w.nr_seq_parametro;

	end loop;
	close c03;
	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_tabela_custo_mes ( cd_estabelecimento_p bigint, dt_mes_referencia_p timestamp, ie_orcado_real_p text, ds_tabela_custo_p text, nm_usuario_p text) FROM PUBLIC;

