CREATE OR REPLACE PROCEDURE gerar_tabela_mer ( nm_usuario_p text, nm_tabela_p text, nr_seq_mer_p bigint) AS $body$
DECLARE


nm_integridade_referencial_w	varchar(255);
qt_seq_mer_w			smallint;

C01 CURSOR FOR
	SELECT	NM_INTEGRIDADE_REFERENCIAL
	from	integridade_referencial
	where	nm_tabela = nm_tabela_p;



BEGIN

open C01;
loop
fetch C01 into
	nm_integridade_referencial_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_seq_mer_w
	from	tasy_mer_tabela
	where	nm_tabela = nm_tabela_p;

	if (qt_seq_mer_w = 0) then
		insert into tasy_mer_tabela(nr_sequencia, nr_seq_mer, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			nm_tabela, nr_seq_carga, qt_pos_x,
			qt_pos_y)
		values (nextval('tasy_mer_tabela_seq'), nr_seq_mer_p, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			nm_integridade_referencial_w, 100, 100,
			100);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
