CREATE OR REPLACE PROCEDURE gerar_tabela_perfil ( cd_perfil_p bigint, nm_tabela_p text, nm_usuario_p text) AS $body$
DECLARE


ie_controle_w	varchar(1)	:= 'T';
ds_aplicacao_w	varchar(15);


BEGIN

select	ds_aplicacao
into STRICT	ds_aplicacao_w
from	tabela_sistema
where	nm_tabela = nm_tabela_p;

if (ds_aplicacao_w = 'TasySis') then
	ie_controle_w	:= 'L';
end	if;

insert	into
	tabela_sistema_perfil(	cd_perfil,
				nm_tabela,
				dt_atualizacao,
				nm_usuario,
				ie_controle)
			values (cd_perfil_p,
				nm_tabela_p,
				clock_timestamp(),
				nm_usuario_p,
				ie_controle_w);
commit;

end	;
$body$
