CREATE OR REPLACE PROCEDURE gerar_tabela_sistema_idioma (cd_funcao_p bigint, nr_seq_idioma_p bigint, nm_usuario_p text) AS $body$
DECLARE


    nm_tabela_w       varchar(50);
    nm_atributo_w     varchar(50);
    nr_seq_visao_w    bigint;
    ds_texto_w        varchar(255);
    cd_funcao_w       integer;
    nr_seq_traducao_w bigint;

    c01 CURSOR FOR
        SELECT a.nm_tabela,
               'DS_TITULO',
               a.ds_titulo,
               a.cd_funcao
          from tabela_sistema a
         where a.cd_funcao = cd_funcao_p
           and (a.ds_titulo IS NOT NULL AND a.ds_titulo::text <> '')
           and not exists (SELECT 1
                  from tab_sistema_idioma x
                 where x.nm_tabela = a.nm_tabela
                   and x.nm_atributo = 'DS_TITULO'
                   and coalesce(x.nr_seq_visao::text, '') = ''
                   and x.nr_seq_idioma = nr_seq_idioma_p);

    c02 CURSOR FOR
        SELECT a.nm_tabela,
               'DS_TITULO',
               a.nr_sequencia,
               a.ds_titulo,
               a.cd_funcao
          from tabela_visao a
         where a.cd_funcao = cd_funcao_p
           and (a.ds_titulo IS NOT NULL AND a.ds_titulo::text <> '')
           and not exists (SELECT 1
                  from tab_sistema_idioma x
                 where x.nm_tabela = a.nm_tabela
                   and x.nm_atributo = 'DS_TITULO'
                   and x.nr_seq_visao = a.nr_sequencia
                   and x.nr_seq_idioma = nr_seq_idioma_p);


BEGIN
    if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (nr_seq_idioma_p IS NOT NULL AND nr_seq_idioma_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
        begin
            open c01;
            loop
                fetch c01
                    into nm_tabela_w,
                         nm_atributo_w,
                         ds_texto_w,
                         cd_funcao_w;
                EXIT WHEN NOT FOUND; /* apply on c01 */
                begin
                    if ((trim(both ds_texto_w) IS NOT NULL AND (trim(both ds_texto_w))::text <> '')) then
                        begin
                            $if $$tasy_local_dict=true $then
                            nr_seq_traducao_w := get_remote_sequence('seq:tab_sistema_idioma_seq');
                            $else
                            select nextval('tab_sistema_idioma_seq') into STRICT nr_seq_traducao_w;
                            $end

                            insert into tab_sistema_idioma(nr_sequencia,
                                 dt_atualizacao_nrec,
                                 nm_usuario_nrec,
                                 dt_atualizacao,
                                 nm_usuario,
                                 nm_tabela,
                                 nm_atributo,
                                 nr_seq_visao,
                                 ds_descricao,
                                 cd_funcao,
                                 nr_seq_idioma,
                                 ds_traducao,
                                 ie_necessita_revisao)
                            values (nr_seq_traducao_w,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 nm_tabela_w,
                                 nm_atributo_w,
                                 null,
                                 ds_texto_w,
                                 cd_funcao_w,
                                 nr_seq_idioma_p,
                                 ' ',
                                 'T');
                        end;
                    end if;
                end;
            end loop;
            close c01;

            open c02;
            loop
                fetch c02
                    into nm_tabela_w,
                         nm_atributo_w,
                         nr_seq_visao_w,
                         ds_texto_w,
                         cd_funcao_w;
                EXIT WHEN NOT FOUND; /* apply on c02 */
                begin
                    if ((trim(both ds_texto_w) IS NOT NULL AND (trim(both ds_texto_w))::text <> '')) then
                        begin

                            $if $$tasy_local_dict=true $then
                            nr_seq_traducao_w := get_remote_sequence('seq:tab_sistema_idioma_seq');
                            $else
                            select nextval('tab_sistema_idioma_seq') into STRICT nr_seq_traducao_w;
                            $end

                            insert into tab_sistema_idioma(nr_sequencia,
                                 dt_atualizacao_nrec,
                                 nm_usuario_nrec,
                                 dt_atualizacao,
                                 nm_usuario,
                                 nm_tabela,
                                 nm_atributo,
                                 nr_seq_visao,
                                 ds_descricao,
                                 cd_funcao,
                                 nr_seq_idioma,
                                 ds_traducao,
                                 ie_necessita_revisao)
                            values (nr_seq_traducao_w,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 clock_timestamp(),
                                 nm_usuario_p,
                                 nm_tabela_w,
                                 nm_atributo_w,
                                 nr_seq_visao_w,
                                 ds_texto_w,
                                 cd_funcao_w,
                                 nr_seq_idioma_p,
                                 ' ',
                                 'T');
                        end;
                    end if;
                end;
            end loop;
            close c02;
        end;
    end if;
    commit;
end;
$body$
