CREATE OR REPLACE PROCEDURE gerar_taxa_ano (ds_ano_ref_p text, nm_usuario_p text) AS $body$
DECLARE


nr_mes_w	integer := 1;
nr_mes_ww	varchar(2);
dt_ref_w	timestamp;

BEGIN
if (coalesce(ds_ano_ref_p,0) > 1990) then

	dt_ref_w := to_date('01/01/'||substr(ds_ano_ref_p,1,4), 'dd/mm/yyyy');
	while(nr_mes_w <= 12) loop
		begin
		if	PKG_DATE_UTILS.start_of(clock_timestamp(),'month',0) = dt_ref_w then
			nr_mes_w := 13;
		else
			CALL Atualizar_Resumo_Geral_Setor(dt_ref_w,nm_usuario_p);
		end if;
		nr_mes_w := nr_mes_w + 1;
		dt_ref_w := PKG_DATE_UTILS.ADD_MONTH(dt_ref_w,1,0);
		end;
	end loop;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
