SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_tempos_cirurgia (nr_cirurgia_p bigint, nm_usuario_p text) AS $body$
DECLARE



c01 CURSOR FOR
	SELECT vl_dominio
	FROM   VALOR_DOMINIo
	WHERE  cd_dominio = 1017
	AND    ie_situacao = 'A';

ie_tipo_tempo_w		varchar(3);
nr_sequencia_w		integer;



BEGIN
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		ie_tipo_tempo_w	;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	coalesce(max(nr_sequencia),0)+1
		into STRICT	nr_sequencia_w
		from	cirurgia_tempo
		where	nr_cirurgia = nr_cirurgia_p;

		insert into cirurgia_tempo(nr_sequencia,
				nr_cirurgia,
				ie_tipo_tempo,
				dt_atualizacao,
				nm_usuario,
				dt_inicial,
				dt_final)
		values (nr_sequencia_w,
				nr_cirurgia_p,
				ie_tipo_tempo_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				clock_timestamp());
		end;
	end loop;
	close c01;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_tempos_cirurgia (nr_cirurgia_p bigint, nm_usuario_p text) FROM PUBLIC;

