CREATE OR REPLACE PROCEDURE gerar_texto_evolucao_cons ( ie_evolucao_clinica_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint, cd_evolucao_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_texto_w	bigint;
ds_texto_w	varchar(32000);
ds_texto_aux_w	text;


BEGIN 
if (cd_evolucao_p IS NOT NULL AND cd_evolucao_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_evolucao_clinica_p IS NOT NULL AND ie_evolucao_clinica_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	nr_seq_texto_w := obter_texto_padrao_evolucao(ie_evolucao_clinica_p,cd_pessoa_fisica_p,nr_atendimento_p);
	 
	if (nr_seq_texto_w IS NOT NULL AND nr_seq_texto_w::text <> '') and (nr_seq_texto_w <> 0) then 
		begin 
		 
		Begin 
				 
		select	ds_texto 
		into STRICT	ds_texto_w 
		from	texto_padrao 
		where	nr_sequencia = nr_seq_texto_w;
		 
		if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') and (ds_texto_w <> '') then 
			begin 
			 
			select	ds_evolucao 
			into STRICT	ds_texto_aux_w 
			from	evolucao_paciente 
			where	cd_evolucao = cd_evolucao_p;
			 
			update	evolucao_paciente 
			set	ds_evolucao	= ds_texto_w || chr(13) || chr(10) || ds_texto_aux_w, 
				nm_usuario	= nm_usuario_p 
			where	cd_evolucao 	= cd_evolucao_p;
			 
			end;
		end if;
		 
		exception 
        when others then	 
 
				ds_texto_w := '';
			 
			/*COPIA_CAMPO_LONG_DE_PARA_NOVO(	'TEXTO_PADRAO', 
					'DS_TEXTO', 
					' where nr_sequencia = :nr_seq_texto', 
					'nr_seq_texto='||nr_seq_texto_w, 
					'EVOLUCAO_PACIENTE', 
					'DS_EVOLUCAO', 
					' where cd_evolucao = :cd_evolucao', 
					'cd_evolucao='||cd_evolucao_p, 
					'L');	 
					commit;*/
 
		 
		end;
 
		 
		end;
	end if;	
	end;
end if;
commit;
end;
