CREATE OR REPLACE PROCEDURE gerar_texto_padrao_ocorrencia ( cd_tipo_ocorrencia_p bigint, nr_ocorrencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_texto_w	bigint;
ds_texto_w	text;
ds_texto_aux_w	text;


BEGIN

if (cd_tipo_ocorrencia_p IS NOT NULL AND cd_tipo_ocorrencia_p::text <> '') and (nr_ocorrencia_p IS NOT NULL AND nr_ocorrencia_p::text <> '') then

	nr_seq_texto_w := obter_texto_padrao_ocorrencia(cd_tipo_ocorrencia_p);


	if (nr_seq_texto_w IS NOT NULL AND nr_seq_texto_w::text <> '') and (nr_seq_texto_w <> 0) then
		begin

		select	ds_texto
		into STRICT	ds_texto_w
		from	texto_padrao
		where	nr_sequencia = nr_seq_texto_w;

		if (ds_texto_w IS NOT NULL AND ds_texto_w::text <> '') and (ds_texto_w <> '') then
			begin

			update	ocorrencia
			set		ds_ocorrencia 	= ds_texto_w,
					nm_usuario		= nm_usuario_p
			where	nr_ocorrencia  	= nr_ocorrencia_p;

			end;
		end if;
		end;
	end if;


end if;

commit;

end;
$body$
