SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_titulo_cheque_atend (ds_lista_cobrancas_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w			cobranca.nr_sequencia%type;
nr_titulo_w				cobranca.nr_titulo%type;
nr_seq_cheque_w			cobranca.nr_seq_cheque%type;
ds_lista_cobr_w			varchar(4000);
qt_w					bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
			a.nr_titulo,
			a.nr_seq_cheque
	from	cobranca a
	where	' ' || ds_lista_cobr_w || ' ' like '% ' || a.nr_sequencia || ' %';


BEGIN

ds_lista_cobr_w	:= substr(ds_lista_cobrancas_p,1,4000);

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_titulo_w,
	nr_seq_cheque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

			select 	count(*)
			into STRICT	qt_w
			from	titulo_receber_atend
			where	nr_seq_atendimento	=	nr_atendimento_p
			and		nr_titulo			=	nr_titulo_w;

			if (qt_w = 0) then

				insert into	titulo_receber_atend(	dt_atualizacao,
													dt_atualizacao_nrec,
													nm_usuario,
													nm_usuario_nrec,
													nr_seq_atendimento,
													nr_sequencia,
													nr_titulo )
										values (	clock_timestamp(),
													clock_timestamp(),
													nm_usuario_p,
													nm_usuario_p,
													nr_atendimento_p,
													nextval('titulo_receber_atend_seq'),
													nr_titulo_w );
			end if;


		elsif (nr_seq_cheque_w IS NOT NULL AND nr_seq_cheque_w::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

			select 	count(*)
			into STRICT	qt_w
			from	cheque_cr_atend
			where	nr_seq_atendimento	=	nr_atendimento_p
			and		nr_seq_cheque		=	nr_seq_cheque_w;

			if (qt_w = 0) then

				insert into	cheque_cr_atend(	dt_atualizacao,
													dt_atualizacao_nrec,
													nm_usuario,
													nm_usuario_nrec,
													nr_seq_atendimento,
													nr_sequencia,
													nr_seq_cheque )
										values (	clock_timestamp(),
													clock_timestamp(),
													nm_usuario_p,
													nm_usuario_p,
													nr_atendimento_p,
													nextval('cheque_cr_atend_seq'),
													nr_seq_cheque_w );

			end if;
		end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_titulo_cheque_atend (ds_lista_cobrancas_p text, nr_atendimento_p bigint, nm_usuario_p text) FROM PUBLIC;

