SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_titulo_pagar_adiant_rep (nr_repasse_terceiro_p bigint, nr_adiantamento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
vl_adiantamento_w	double precision;
nr_sequencia_w		bigint;
nr_titulo_w		bigint;
vl_saldo_titulo_w	double precision;
ds_observacao_w		adiantamento_pago.ds_observacao%type;
				

BEGIN 
 
if (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '') and (nr_repasse_terceiro_p IS NOT NULL AND nr_repasse_terceiro_p::text <> '') then 
	 
	select	max(b.nr_titulo) 
	into STRICT	nr_titulo_w 
	from	repasse_terceiro_venc b, 
		repasse_terceiro a 
	where	a.nr_repasse_terceiro	= b.nr_repasse_terceiro 
	and	a.nr_repasse_terceiro	= nr_repasse_terceiro_p;
	 
	select	sum(a.vl_saldo) 
	into STRICT	vl_adiantamento_w 
	from	adiantamento_pago a 
	where	a.nr_adiantamento	= nr_adiantamento_p;
	 
	select 	sum(a.vl_saldo_titulo) 
	into STRICT	vl_saldo_titulo_w 
	from	titulo_pagar a 
	where	nr_titulo = nr_titulo_w;
	 
	select	coalesce(substr(ds_observacao,1,255),'') 
	into STRICT	ds_observacao_w 
	from	adiantamento_pago 
	where	nr_adiantamento 	= nr_adiantamento_p;
	 
	if ( coalesce(vl_adiantamento_w,0) > coalesce(vl_saldo_titulo_w,0) ) then -- Se o valor do adiantamento for maior que o valor do titulo, o valor do adiantamento vinculado sera o valor do titulo AAMFIRMO OS 684176 
		vl_adiantamento_w := coalesce(vl_saldo_titulo_w,0);	
	end if;
	 
	select	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_sequencia_w 
	from	titulo_pagar_adiant a 
	where	a.nr_titulo	= nr_titulo_w;
	 
	insert 	into titulo_pagar_adiant(nr_sequencia, 
			nr_titulo, 
			nr_adiantamento, 
			vl_adiantamento, 
			dt_atualizacao, 
			nm_usuario, 
			ds_observacao) 
		values (	nr_sequencia_w, 
			nr_titulo_w, 
			nr_adiantamento_p, 
			vl_adiantamento_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			ds_observacao_w);
	 
	CALL atualiza_adiantamento_pago(nr_titulo_w,nr_adiantamento_p,vl_adiantamento_w,nr_sequencia_w,nm_usuario_p,'I');	
	 
	update	repasse_terceiro_venc 
	set	vl_liquido		= vl_liquido - coalesce(vl_adiantamento_w,0) 
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p 
	and	nr_titulo		= nr_titulo_w;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
