SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_titulo_pagar_bordero_nc (nr_seq_bordero_p bigint, nm_usuario_p text, nr_titulo_p INOUT bigint) AS $body$
DECLARE

 
vl_nota_credito_w	double precision;
cd_estabelecimento_w	smallint;
cd_cgc_w		varchar(14);
cd_moeda_w		integer;
cd_tipo_taxa_juro_w	bigint;
cd_tipo_taxa_multa_w	bigint;
nr_titulo_w		bigint;
cd_conta_financ_w	bigint;
cd_conta_contabil_w	varchar(20);
nr_seq_classif_w	bigint;


BEGIN 
 
select	a.cd_estabelecimento, 
	a.cd_cgc, 
	a.cd_conta_contabil, 
	a.cd_conta_financ 
into STRICT	cd_estabelecimento_w, 
	cd_cgc_w, 
	cd_conta_contabil_w, 
	cd_conta_financ_w 
from	bordero_nc a 
where	a.nr_sequencia		= nr_seq_bordero_p;
 
select	sum(a.vl_nota_credito) 
into STRICT	vl_nota_credito_w 
from	bordero_nc_nota_credito a 
where	a.nr_seq_bordero	= nr_seq_bordero_p;
 
select	max(a.cd_moeda_padrao), 
	max(a.cd_tipo_taxa_juro), 
	max(a.cd_tipo_taxa_multa) 
into STRICT	cd_moeda_w, 
	cd_tipo_taxa_juro_w, 
	cd_tipo_taxa_multa_w 
from	parametros_contas_pagar a 
where	a.cd_estabelecimento	= cd_estabelecimento_w;
 
select	nextval('titulo_pagar_seq') 
into STRICT	nr_titulo_w
;
 
insert	into	titulo_pagar(nr_titulo, 
	nm_usuario, 
	dt_atualizacao, 
	cd_estabelecimento, 
	dt_emissao, 
	dt_vencimento_original, 
	dt_vencimento_atual, 
	vl_titulo, 
	vl_saldo_titulo, 
	vl_saldo_juros, 
	vl_saldo_multa, 
	cd_moeda, 
	tx_juros, 
	tx_multa, 
	cd_tipo_taxa_juro, 
	cd_tipo_taxa_multa, 
	ie_situacao, 
	ie_origem_titulo, 
	ie_tipo_titulo, 
	cd_cgc, 
	nr_seq_bordero_nc, 
	ds_observacao_titulo) 
values (nr_titulo_w, 
	nm_usuario_p, 
	clock_timestamp(), 
	cd_estabelecimento_w, 
	trunc(clock_timestamp(),'dd'), 
	trunc(clock_timestamp(),'dd') + 15, 
	trunc(clock_timestamp(),'dd') + 15, 
	vl_nota_credito_w, 
	vl_nota_credito_w, 
	0, 
	0, 
	cd_moeda_w, 
	0, 
	0, 
	cd_tipo_taxa_juro_w, 
	cd_tipo_taxa_multa_w, 
	'A', 
	11, 
	21, 
	cd_cgc_w, 
	nr_seq_bordero_p, 
	--'Título gerado a partir do borderô de nota de crédito ' || nr_seq_bordero_p 
	wheb_mensagem_pck.get_texto(303648,'NR_SEQ_BORDERO_P='||nr_seq_bordero_p));
CALL ATUALIZAR_INCLUSAO_TIT_PAGAR(nr_titulo_w, nm_usuario_p);
 
select	coalesce(max(a.nr_sequencia),0) + 1 
into STRICT	nr_seq_classif_w 
from	titulo_pagar_classif a;
 
if (cd_conta_contabil_w IS NOT NULL AND cd_conta_contabil_w::text <> '') or (cd_conta_financ_w IS NOT NULL AND cd_conta_financ_w::text <> '') then 
 
	insert	into titulo_pagar_classif(cd_conta_contabil, 
		dt_atualizacao, 
		nm_usuario, 
		nr_Seq_conta_financ, 
		nr_sequencia, 
		nr_titulo, 
		vl_titulo) 
	values (cd_conta_contabil_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		cd_conta_financ_w, 
		nr_seq_classif_w, 
		nr_titulo_w, 
		vl_nota_credito_w);
 
end if;
 
update	bordero_nc 
set	dt_geracao_titulo	= clock_timestamp() 
where	nr_sequencia		= nr_seq_bordero_p;
 
nr_titulo_p	:= nr_titulo_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_titulo_pagar_bordero_nc (nr_seq_bordero_p bigint, nm_usuario_p text, nr_titulo_p INOUT bigint) FROM PUBLIC;

