SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_titulo_pagar_resp_pagto ( nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_cgc_w			varchar(255);
cd_cgc_congenere_w		varchar(255);
nr_seq_congenere_w		bigint;


BEGIN

select	max(cd_cgc)
into STRICT	cd_cgc_w
from	titulo_pagar
where	nr_titulo	= nr_titulo_p;

if (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_congenere_w
	from	pls_congenere a
	where	a.cd_cgc	= cd_cgc_w;

	if (nr_seq_congenere_w IS NOT NULL AND nr_seq_congenere_w::text <> '') then
		cd_cgc_congenere_w	:= pls_obter_coop_pag_resp_fin(	nr_seq_congenere_w, clock_timestamp());
		if (cd_cgc_congenere_w IS NOT NULL AND cd_cgc_congenere_w::text <> '') then
			insert into titulo_pagar_resp_pagto(NR_SEQUENCIA,
				DT_ATUALIZACAO,
				NM_USUARIO,
				DT_ATUALIZACAO_NREC,
				NM_USUARIO_NREC,
				NR_TITULO,
				CD_CGC,
				CD_PESSOA_FISICA)
			SELECT	nextval('titulo_pagar_resp_pagto_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_titulo_p,
				cd_cgc_congenere_w,
				null
			;
		end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
