SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_titulo_receber_nc (nr_titulo_p bigint, nr_seq_nota_credito_p bigint, vl_nota_credito_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


/* ie_opcao_p
'V'	Vincular nota de crédito ao título
'D'	Desvincular nota de crédito do título
*/
nr_seq_retorno_w	bigint;
ie_estornar_ret_w	varchar(10);


BEGIN

if (ie_opcao_p	= 'V') then

	insert	into titulo_receber_nc(dt_atualizacao,
		nm_usuario,
		nr_seq_nota_credito,
		nr_sequencia,
		nr_titulo_rec,
		vl_nota_credito)
	values (clock_timestamp(),
		nm_usuario_p,
		nr_seq_nota_credito_p,
		nextval('titulo_receber_nc_seq'),
		nr_titulo_p,
		vl_nota_credito_p);

elsif (ie_opcao_p	= 'D') then

	ie_estornar_ret_w := obter_param_usuario(801, 178, wheb_usuario_pck.get_cd_perfil, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_estornar_ret_w);

	if (coalesce(ie_estornar_ret_w,'S') = 'N') then
		select	max(b.nr_sequencia)
		into STRICT	nr_seq_retorno_w
		from	convenio_retorno b,
			convenio_retorno_item a
		where	b.nr_sequencia		= a.nr_seq_retorno
		and	b.ie_status_retorno	<> 'F'
		and	a.vl_nota_credito	<> 0
		and	a.nr_titulo		= nr_titulo_p
		and	exists (SELECT	1
			from	titulo_receber_nc x
			where	x.nr_titulo_rec		= a.nr_titulo
			and	x.nr_seq_nota_credito	= nr_seq_nota_credito_p);

		if (coalesce(nr_seq_retorno_w,0) > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(248860,'NR_SEQ_RETORNO_W=' || nr_seq_retorno_w );
			/*Não foi possível desvincular a nota de crédito do título!
			Esta nota de crédito já foi rateada no retorno: '|| nr_seq_retorno_w*/
		end if;
	end if;

	delete	from titulo_receber_nc
	where	nr_seq_nota_credito	= nr_seq_nota_credito_p
	and	nr_titulo_rec		= nr_titulo_p;

end if;

CALL atualizar_saldo_nota_credito(nr_seq_nota_credito_p,nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
