CREATE OR REPLACE PROCEDURE gerar_tit_classif_nf ( nr_titulo_p bigint, nr_seq_nota_p bigint, nr_item_nf_p bigint, ie_deletar_todos_p text, nm_usuario_p text) AS $body$
DECLARE


cd_conta_contabil_w	varchar(20);
cd_centro_custo_w	integer;
cd_conta_financ_w	bigint;
vl_rateio_w		double precision;
vl_desconto_w		double precision;
nr_seq_tit_w		integer;

C01 CURSOR FOR
	SELECT	cd_conta_contabil,
		cd_centro_custo,
		cd_conta_financ,
		coalesce(vl_rateio,0),
		coalesce(vl_desconto,0)
	from	nota_fiscal_item_rateio
	where	nr_seq_nota	= nr_seq_nota_p
	and	nr_item_nf	= nr_item_nf_p
	
union all

	SELECT	cd_conta_contabil,
		cd_centro_custo,
		nr_seq_conta_financ,
		coalesce(vl_liquido,0),
		coalesce(vl_desconto,0)
	from	nota_fiscal_item a
	where	nr_sequencia	= nr_seq_nota_p
	and	nr_item_nf	= nr_item_nf_p
	and	not exists (select	1
			from	nota_fiscal_item_rateio x
			where	x.nr_seq_nota	= nr_seq_nota_p
			and	x.nr_item_nf	= nr_item_nf_p);


BEGIN

if (ie_deletar_todos_p = 'S') then
	delete	from titulo_pagar_classif
	where	nr_titulo	= nr_titulo_p;
end if;

open C01;
loop
fetch C01 into
	cd_conta_contabil_w,
	cd_centro_custo_w,
	cd_conta_financ_w,
	vl_rateio_w,
	vl_desconto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_seq_tit_w
	from	titulo_pagar_classif
	where	nr_titulo	= nr_titulo_p;

	insert	into titulo_pagar_classif(cd_centro_custo, cd_conta_contabil, dt_atualizacao, nm_usuario, nr_contrato,
		nr_seq_conta_financ, nr_seq_trans_fin, nr_sequencia, nr_titulo, vl_acrescimo,
		vl_desconto, vl_original, vl_titulo)
	values (cd_centro_custo_w, cd_conta_contabil_w, clock_timestamp(), nm_usuario_p, null,
		cd_conta_financ_w, null, nr_seq_tit_w, nr_titulo_p, null,
		vl_desconto_w, null, vl_rateio_w);
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
