CREATE OR REPLACE PROCEDURE gerar_tit_rec_cobr_instrucao ( nr_seq_cobranca_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_instrucao_w		double precision;
cd_instrucao_w		varchar(5);
cd_banco_w		smallint;
nr_seq_tit_rec_cobr_w	bigint;

c01 CURSOR FOR
SELECT	cd_instrucao
from	instrucao_escritural
where	cd_banco 	= cd_banco_w
and	ie_desc_prev 	= 'S';


BEGIN

select	max(cd_banco)
into STRICT	cd_banco_w
from	cobranca_escritural
where	nr_sequencia = nr_seq_cobranca_p;

select	max(nr_sequencia)
into STRICT	nr_seq_tit_rec_cobr_w
from	titulo_receber_cobr
where	nr_titulo = nr_titulo_p;

select	coalesce(sum(a.vl_saldo_titulo),0)
into STRICT	vl_instrucao_w
from	titulo_receber_nc b,
	titulo_pagar a
where	a.nr_titulo 	= b.nr_titulo_pagar
and	b.nr_titulo_rec 	= nr_titulo_p;

open C01;
loop
fetch C01 into
	cd_instrucao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	if (vl_instrucao_w > 0) then

		insert into titulo_rec_cobr_instrucao(nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_tit_rec_cobr,
							cd_instrucao,
							vl_desc_prev)
						values (nextval('titulo_rec_cobr_instrucao_seq'),
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_tit_rec_cobr_w,
							cd_instrucao_w,
							vl_instrucao_w);
		commit;
	end if;

end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
