CREATE OR REPLACE PROCEDURE gerar_todos_rotina_antib ( nr_prescricao_p bigint, nr_seq_item_p bigint, nr_seq_lista_cid_p text, nm_usuario_p text) AS $body$
DECLARE



nr_seq_lista_w		varchar(255);
nr_pos_virgula_w	bigint;
nr_sequencia_w		bigint;


BEGIN
if (nr_seq_lista_cid_p IS NOT NULL AND nr_seq_lista_cid_p::text <> '')	then
	begin
	nr_seq_lista_w	:= nr_seq_lista_cid_p;
	while(nr_seq_lista_w IS NOT NULL AND nr_seq_lista_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in nr_seq_lista_w);
		if (nr_pos_virgula_w > 0)	then
			begin
			nr_sequencia_w	:= (substr(nr_seq_lista_w,1,nr_pos_virgula_w-1))::numeric;
			nr_seq_lista_w	:= substr(nr_seq_lista_w,nr_pos_virgula_w+1,length(nr_seq_lista_w));
			if (nr_sequencia_w > 0) then
				begin
					CALL gerar_cid_rotina_antib_prescr(nr_prescricao_p, nr_seq_item_p, nr_sequencia_w, nm_usuario_p);
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	end;
end if;

commit;

end;
$body$
