SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_toxidade_sae ((nr_sequencia_p bigint, nm_usuario_p text) is nr_atendimento_w bigint) RETURNS varchar AS $body$
DECLARE


	ie_retorno_w	varchar(15);
	nr_seq_result_escala_w	varchar(15);
	
BEGIN

	select	max(f.nr_seq_result_escala)
	into STRICT	nr_seq_result_escala_w
	from	pe_prescricao a,
		pe_prescr_item_result b,
		pe_regra_item_escala c,
		pe_regra_result_escala f
	where	a.nr_sequencia = nr_sequencia_p
	and	a.nr_sequencia = b.nr_seq_prescr
	and	b.nr_seq_item = c.nr_seq_item_sae
	and	c.nr_seq_item_escala = nr_seq_item_escala_p
	and	f.nr_seq_result_sae = b.nr_seq_result
	and	f.nr_seq_regra = c.nr_sequencia;

	select 	max(x.vl_result)
	into STRICT	ie_retorno_w
	from	pe_result_item x,
		pe_regra_result_escala f
	where	x.nr_sequencia = nr_seq_result_escala_w;



	return coalesce(ie_retorno_w,'G0');
	end;

begin

select	max(a.cd_prescritor),
	max(a.nr_atendimento)
into STRICT	cd_profissional_w,
	nr_atendimento_w
from	pe_prescricao a,
	pe_prescr_item_result b
where	a.nr_sequencia = b.nr_seq_prescr
and	a.nr_sequencia = nr_sequencia_p;

select	coalesce(max('S'),'N')
into STRICT	ie_possui_registro_w
from 	pe_prescricao a,
	pe_prescr_item_result b,
	pe_regra_item_escala c,
	pe_regra_result_escala d
where	a.nr_sequencia	= b.nr_seq_prescr
and	b.nr_seq_result = d.nr_seq_result_sae
and	b.nr_seq_item	= c.nr_seq_item_sae
and	a.nr_sequencia	= nr_sequencia_p
and	(c.nr_seq_item_escala IS NOT NULL AND c.nr_seq_item_escala::text <> '')
and 	c.ie_escala = 105;

if (ie_possui_registro_w = 'S') then
	begin
	ie_hemoglobina_w	:= obter_valor_escala(92);
	ie_leucocito_w		:= obter_valor_escala(93);
	ie_granulocito_w	:= obter_valor_escala(94);
	ie_plaqueta_w		:= obter_valor_escala(95);
	ie_mucosite_w		:= obter_valor_escala(96);
	ie_nausea_w		:= obter_valor_escala(97);
	ie_vomito_w		:= obter_valor_escala(98);
	ie_diarreia_w		:= obter_valor_escala(99);
	ie_obstipacao_w		:= obter_valor_escala(100);
	ie_anorexia_w		:= obter_valor_escala(101);
	ie_disgeusia_w		:= obter_valor_escala(102);
	ie_alopecia_w		:= obter_valor_escala(103);
	ie_sindrome_mao_pe_w	:= obter_valor_escala(104);
	ie_fadiga_w		:= obter_valor_escala(105);
	ie_insonia_w		:= obter_valor_escala(106);
	ie_disuria_w		:= obter_valor_escala(107);
	end;

	select a.qt_peso,
		a.qt_altura_cm,
		a.qt_imc
	into STRICT    qt_peso_w,
		qt_altura_cm_w,
		qt_imc_w
	from atendimento_sinal_vital a
	where a.nr_sequencia = (SELECT max(b.nr_sequencia)
					  from atendimento_sinal_vital b
					  where b.nr_atendimento = nr_atendimento_w
					  and (b.qt_peso IS NOT NULL AND b.qt_peso::text <> '')
					  and (b.qt_altura_cm IS NOT NULL AND b.qt_altura_cm::text <> '')
					  and (b.qt_imc IS NOT NULL AND b.qt_imc::text <> ''))
	order by dt_sinal_vital desc;


	insert into escala_toxidade(nr_sequencia,
				cd_profissional,
				dt_atualizacao,
				dt_avaliacao,
				ie_hemoglobina,
				ie_leucocito,
				ie_granulocito,
				ie_plaqueta,
				ie_mucosite,
				ie_nausea,
				ie_vomito,
				ie_diarreia,
				ie_obstipacao,
				ie_anorexia,
				ie_disgeusia,
				ie_alopecia,
				ie_sindrome_mao_pe,
				ie_fadiga,
				ie_insonia,
				ie_disuria,
				qt_peso,
				qt_altura_cm,
				qt_imc,
				nm_usuario,
				nr_atendimento,
				ie_situacao,
				dt_liberacao)
	values (	nextval('escala_toxidade_seq'),
				cd_profissional_w,
				clock_timestamp(),
				clock_timestamp(),
				ie_hemoglobina_w,
				ie_leucocito_w,
				ie_granulocito_w,
				ie_plaqueta_w,
				ie_mucosite_w,
				ie_nausea_w,
				ie_vomito_w,
				ie_diarreia_w,
				ie_obstipacao_w,
				ie_anorexia_w,
				ie_disgeusia_w,
				ie_alopecia_w,
				ie_sindrome_mao_pe_w,
				ie_fadiga_w,
				ie_insonia_w,
				ie_disuria_w,
				coalesce(qt_peso_w,0),
				coalesce(qt_altura_cm_w,0),
				coalesce(qt_imc_w,0),
				nm_usuario_p,
				nr_atendimento_w,
				'A',
				clock_timestamp());

end if;


if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
