CREATE OR REPLACE PROCEDURE gerar_transferencia_bem ( cd_imobilizado_p text, nr_seq_local_manutencao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text, nr_seq_tipo_hist_p bigint default null) AS $body$
DECLARE

nr_seq_local_novo_w	bigint;
nr_sequencia_bem_w	bigint;
nr_seq_local_w		bigint;
ds_erro_w		varchar(100);


BEGIN

ds_erro_w := null;

if (cd_imobilizado_p IS NOT NULL AND cd_imobilizado_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),0),
		coalesce(max(nr_seq_local),0)
	into STRICT	nr_sequencia_bem_w,
		nr_seq_local_w
	from	pat_bem
	where	cd_bem		= cd_imobilizado_p
	and	cd_estabelecimento	= coalesce(cd_estabelecimento_p,cd_estabelecimento);
	end;

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_local_novo_w
	from	pat_local
	where	nr_seq_local_manutencao = nr_seq_local_manutencao_p;

	if (nr_seq_local_novo_w <> 0) and (nr_sequencia_bem_w <> 0) and (nr_seq_local_novo_w <> nr_seq_local_w) then
		ds_erro_w := gerar_transf_patrimonio(nr_sequencia_bem_w, clock_timestamp(), nr_seq_local_novo_w, null, null, null, nm_usuario_p, ds_erro_w, null, nr_seq_tipo_hist_p, 'S');
	end if;
end if;

ds_erro_p := ds_erro_w;

END;
