SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_transferencia_local_pa ( nr_atendimento_p bigint, nr_seq_local_pa_p bigint, nm_usuario_p text) AS $body$
DECLARE

cd_setor_transferencia_w		bigint;
cd_unidade_basica_w	varchar(10);
cd_unidade_compl_w	varchar(10);
cd_tipo_acomodacao_w	smallint;
dt_entrada_unidade_w	timestamp;
nr_atend_atual_w		atendimento_paciente.nr_atendimento%type;

C01 CURSOR FOR
	SELECT	a.cd_unidade_basica,
		a.cd_unidade_compl,
		a.cd_tipo_acomodacao
	from	tipo_acomodacao b, unidade_atendimento a 
	where	a.cd_setor_atendimento	= cd_setor_transferencia_w
	and	a.cd_tipo_acomodacao	= b.cd_tipo_acomodacao
	and	b.ie_sem_acomodacao = 'S' 
	and	a.ie_situacao	= 'A'
	and	a.ie_status_unidade	= 'L'
	and a.nr_seq_local_pa = nr_seq_local_pa_p
	and	coalesce(a.nr_atendimento::text, '') = ''
	and a.nr_seq_interno <> coalesce(Obter_Unidade_Atendimento(nr_atend_atual_w,'A','NR'),0);


BEGIN

if (nr_seq_local_pa_p IS NOT NULL AND nr_seq_local_pa_p::text <> '') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')then	
	
	select	max(cd_setor_transferencia)
	into STRICT	cd_setor_transferencia_w
	from	pa_local
	where	nr_sequencia	= nr_seq_local_pa_p;
	
	if (cd_setor_transferencia_w IS NOT NULL AND cd_setor_transferencia_w::text <> '') and (cd_setor_transferencia_w	<> Obter_Setor_Atendimento(nr_atendimento_p))then
		
		select	coalesce(max(nr_atendimento),0)
		into STRICT nr_atend_atual_w
		from	atendimento_paciente a
		where	a.nr_seq_local_pa = nr_seq_local_pa_p
		and a.nr_atendimento <> nr_atendimento_p
		and coalesce(a.dt_alta::text, '') = '';
		
		open C01;
		loop
		fetch C01 into	
			cd_unidade_basica_w,	
			cd_unidade_compl_w,
			cd_tipo_acomodacao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			exit;
		end loop;
		close C01;
		
		if (cd_unidade_basica_w IS NOT NULL AND cd_unidade_basica_w::text <> '') then
			dt_entrada_unidade_w	:= clock_timestamp();
			CALL gerar_transferencia_paciente(nr_atendimento_p, cd_setor_transferencia_w, cd_unidade_basica_w, cd_unidade_compl_w, cd_tipo_acomodacao_w, 0, null, null, nm_usuario_p, dt_entrada_unidade_w);

		end if;
		
	end if;
	
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
