SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_transferencia_pac_quimio (nr_atendimento_p bigint, cd_setor_atendimento_p bigint, cd_unidade_basica_p text, nm_usuario_p text ) AS $body$
DECLARE


nr_sequencia_local_w    bigint;
ie_controla_ocupacao_w  varchar(10);


BEGIN

ie_controla_ocupacao_w := obter_param_usuario(3130, 492, Obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_controla_ocupacao_w);

if (coalesce(ie_controla_ocupacao_w, 'N') = 'S'
    and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '')
    and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '')
    and (cd_unidade_basica_p IS NOT NULL AND cd_unidade_basica_p::text <> '')) then

    select  max(q.nr_sequencia)
    into STRICT    nr_sequencia_local_w
    from    qt_local q,
            unidade_atendimento ua
    where   q.nr_seq_unidade = ua.nr_seq_interno
    and     ua.cd_setor_atendimento = cd_setor_atendimento_p
    and     q.ie_situacao = 'A'
    and     ua.cd_unidade_basica = cd_unidade_basica_p;

    if (nr_sequencia_local_w IS NOT NULL AND nr_sequencia_local_w::text <> '') then

        update  paciente_atendimento
        set     nr_seq_local = nr_sequencia_local_w,
                nm_usuario = nm_usuario_p,
                dt_atualizacao = clock_timestamp()
        where   nr_atendimento = nr_atendimento_p;

        commit;

    end if;

end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
