CREATE OR REPLACE PROCEDURE gerar_transfusao_reserva_html ( NR_SEQ_RESERVA_P bigint, CD_PF_REALIZOU_P text, NM_USUARIO_P text, IE_DATA_ATUAL_P text, IE_DATA_UTILIZACAO_P text, CD_ESTABELECIMENTO_P bigint, NR_SEQ_TRANSFUSAO_P INOUT bigint ) AS $body$
DECLARE


C01 CURSOR FOR
SELECT c.nr_sequencia
from	san_reserva a,
		SAN_RESERVA_PROD b,
		san_producao c
where 	a.nr_sequencia = b.nr_seq_reserva
and 	b.nr_seq_producao = c.nr_sequencia
and 	a.nr_sequencia = NR_SEQ_RESERVA_P;

BEGIN

For C01_w in C01 loop
        BEGIN
        CALL SAN_VALIDA_HEM_IRRADIADO(C01_w.nr_sequencia);
        end;
end loop;

NR_SEQ_TRANSFUSAO_P := gerar_transfusao_reserva(NR_SEQ_RESERVA_P, CD_PF_REALIZOU_P, NM_USUARIO_P, IE_DATA_ATUAL_P, IE_DATA_UTILIZACAO_P, CD_ESTABELECIMENTO_P, NR_SEQ_TRANSFUSAO_P);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
