CREATE OR REPLACE PROCEDURE gerar_transf_equipamento ( cd_bem_p text, nr_seq_bem_p bigint, nm_usuario_p text, nr_seq_local_novo_p bigint, nr_seq_planej_p bigint, nr_Seq_trab_p bigint) AS $body$
DECLARE


cd_centro_custo_novo_w		bigint;
cd_estabelecimento_w	pat_local.cd_estabelecimento%type;
nr_seq_local_novo_w		bigint;
nr_sequencia_bem_w		bigint;
nr_seq_trab_w			bigint;
nr_seq_planej_w			bigint;


BEGIN
nr_sequencia_bem_w	:= coalesce(nr_seq_bem_p,0);
nr_seq_trab_w		:= nr_seq_trab_p;
nr_seq_planej_w		:= nr_seq_planej_p;

if (nr_sequencia_bem_w = 0) then
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_bem_w
	from	pat_bem
	where	cd_bem 	= cd_bem_p;
end if;
select	nr_seq_local_manutencao,
		cd_centro_custo,
		cd_estabelecimento
into STRICT	nr_seq_local_novo_w,
		cd_centro_custo_novo_w,
		cd_estabelecimento_w
from	pat_local
where	nr_sequencia	= nr_seq_local_novo_p;

if (cd_bem_p IS NOT NULL AND cd_bem_p::text <> '') and (nr_sequencia_bem_w <> 0) and (coalesce(nr_seq_local_novo_w,0) <> 0) then
	begin
	update	man_equipamento
	set	nr_seq_local	= nr_seq_local_novo_w,
		cd_centro_custo	= cd_centro_custo_novo_w,
		cd_estab_contabil = cd_estabelecimento_w,
		nr_seq_planej	= coalesce(CASE WHEN nr_seq_planej_w=0 THEN null  ELSE nr_seq_planej_w END ,nr_seq_planej),
		nr_seq_trab	= coalesce(CASE WHEN nr_seq_trab_w=0 THEN null  ELSE nr_seq_trab_w END ,nr_seq_trab),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	cd_imobilizado	= cd_bem_p;
	commit;
	end;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
