CREATE OR REPLACE PROCEDURE gerar_transporte_paciente ( nr_seq_atendimento_p bigint, nr_seq_paciente_p bigint, ie_tipo_solicitacao_p text, nm_usuario_p text default null) AS $body$
DECLARE


nr_sequencia_w		trans_solicitacao.nr_sequencia%type;
nm_usuario_w		varchar(255);


BEGIN

	if (coalesce(nm_usuario_p::text, '') = '') then
		nm_usuario_w := obter_usuario_ativo;
	else
		nm_usuario_w := nm_usuario_p;
	end if;

	if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '')
		and (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '')
		and (ie_tipo_solicitacao_p IS NOT NULL AND ie_tipo_solicitacao_p::text <> '')
		and (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then
		begin

			select	nextval('trans_solicitacao_seq')
			into STRICT	nr_sequencia_w
			;

			insert into trans_solicitacao(
				nr_sequencia,
				dt_atualizacao,
				dt_solicitacao,
				ie_tipo_solicitacao,
				ie_status,
				nr_atendimento,
				nm_usuario,
				cd_solicitante,
				cd_pessoa_fisica
			)
			values (
				nr_sequencia_w,
				clock_timestamp(),
				clock_timestamp(),
				ie_tipo_solicitacao_p,
				1,
				nr_seq_atendimento_p,
				nm_usuario_w,
				Obter_Pessoa_Fisica_Usuario(nm_usuario_w, 'C'),
				nr_seq_paciente_p
			);
		end;
	end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
