CREATE OR REPLACE PROCEDURE gerar_tributos_repasse ( nr_repasse_terceiro_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
 
nr_seq_venc_w		bigint;
cd_estabelecimento_w	bigint;
ie_trib_venc_repasse_w	varchar(20);

c01 CURSOR FOR 
SELECT	nr_sequencia 
from	repasse_terceiro_venc 
where	nr_repasse_terceiro	= nr_repasse_terceiro_p;


BEGIN 
 
delete 	from repasse_terc_venc_trib 
where	ie_pago_prev	= 'V' 		-- Edgar 19/09/2007, deletar somente previstos 
and	nr_seq_rep_venc in ( 
	SELECT	nr_sequencia 
	from	repasse_terceiro_venc 
	where	nr_repasse_terceiro	= nr_repasse_terceiro_p);
 
open c01;
loop 
fetch c01 into 
	nr_seq_venc_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL Gerar_Tributo_Venc_Repasse(nr_seq_venc_w);
end loop;
close c01;
 
commit;
 
end;
$body$
