SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_tributo_tit_desdob ( nr_titulo_p bigint, nm_usuario_p text, nr_titulo_desdob_p bigint, qt_parcela_p bigint) AS $body$
DECLARE

 
cd_tributo_w		titulo_receber_trib.cd_tributo%type;
vl_base_adic_w		titulo_receber_trib.vl_base_adic%type;
vl_base_calculo_w	titulo_receber_trib.vl_base_calculo%type;
vl_base_nao_retido_w	titulo_receber_trib.vl_base_nao_retido%type;
vl_tributo_w		titulo_receber_trib.vl_tributo%type;
vl_trib_adic_w		titulo_receber_trib.vl_trib_adic%type;
vl_trib_nao_retido_w	titulo_receber_trib.vl_trib_nao_retido%type;
tx_tributo_w		titulo_receber_trib.tx_tributo%type;
nr_seq_nfs_w		titulo_receber_trib.nr_seq_nota_fiscal%type;
ie_origem_trib_w	titulo_receber_trib.ie_origem_tributo%type;
nr_sequencia_w		titulo_receber_trib.nr_sequencia%type;
vl_titulo_w		titulo_receber.vl_saldo_titulo%type;
vl_titulo_desdob_w	titulo_receber.vl_saldo_titulo%type;
nr_titulo_imposto_w	bigint;
nr_seq_tit_rec_trib_w	bigint;
count_w			bigint;
vl_porcentagem_w	double precision;
vl_tributo_atual_w	double precision;

 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.cd_tributo, 
		a.vl_base_adic, 
		a.vl_base_calculo, 
		a.vl_base_nao_retido, 
		a.vl_tributo, 
		a.vl_trib_adic, 
		a.vl_trib_nao_retido, 
		a.tx_tributo, 
		a.ie_origem_tributo, 
		a.nr_seq_nota_fiscal 
	from	titulo_receber_trib a 
	where	a.nr_titulo	= nr_titulo_desdob_p;

C02 CURSOR FOR 
	SELECT x.nr_titulo 
	from titulo_pagar x 
	where x.nr_seq_tit_rec_trib =	nr_sequencia_w;


BEGIN 
 
select	vl_saldo_titulo 
into STRICT	vl_titulo_w 
from	titulo_receber 
where	nr_titulo = nr_titulo_p;
 
select	vl_saldo_titulo 
into STRICT	vl_titulo_desdob_w 
from	titulo_receber 
where	nr_titulo = nr_titulo_desdob_p;
 
open C01;
loop 
fetch C01 into 
	nr_sequencia_w, 
	cd_tributo_w, 
	vl_base_adic_w, 
	vl_base_calculo_w, 
	vl_base_nao_retido_w, 
	vl_tributo_w, 
	vl_trib_adic_w, 
	vl_trib_nao_retido_w, 
	tx_tributo_w, 
	ie_origem_trib_w, 
	nr_seq_nfs_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	if (qt_parcela_p > 0) then 
	 
		select	nextval('titulo_receber_trib_seq') 
		into STRICT	nr_seq_tit_rec_trib_w 
		;
 
		insert into titulo_receber_trib(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			nr_titulo, 
			cd_tributo, 
			vl_tributo, 
			vl_saldo, 
			tx_tributo, 
			vl_base_calculo, 
			vl_trib_nao_retido, 
			vl_base_nao_retido, 
			vl_trib_adic, 
			vl_base_adic, 
			nr_seq_nota_fiscal, 
			ie_origem_tributo) 
		values	(nr_seq_tit_rec_trib_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_titulo_p, 
			cd_tributo_w, 
			((dividir_sem_round((coalesce(vl_base_calculo_w,0) * vl_titulo_w),vl_titulo_desdob_w)) * coalesce(tx_tributo_w,0)) / 100, 
			0, 
			coalesce(tx_tributo_w,0), 
			dividir_sem_round((coalesce(vl_base_calculo_w,0) * vl_titulo_w),vl_titulo_desdob_w), 
			dividir_sem_round((coalesce(vl_trib_nao_retido_w,0) * vl_titulo_w),vl_titulo_desdob_w), 
			dividir_sem_round((coalesce(vl_base_nao_retido_w,0) * vl_titulo_w),vl_titulo_desdob_w), 
			dividir_sem_round((coalesce(vl_trib_adic_w,0) * vl_titulo_w),vl_titulo_desdob_w), 
			dividir_sem_round((coalesce(vl_base_adic_w,0) * vl_titulo_w),vl_titulo_desdob_w), 
			nr_seq_nfs_w, 
			CASE WHEN coalesce(ie_origem_trib_w::text, '') = '' THEN  'C'  ELSE ie_origem_trib_w END );
 
	end if;
 
	end;
end loop;
close C01;
 
open c02; /* francisco - os 60897 - 04/07/2007 - criada a rotina para cancelar os títulos de impostos do título principal */
loop 
fetch c02 into 
	nr_titulo_imposto_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	CALL cancelar_titulo_pagar(nr_titulo_imposto_w,nm_usuario_p, clock_timestamp());
end loop;
close c02;
 
commit;
 
exception when others then 
	null;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
