SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_update_autor_conta ( nr_seq_agenda_p bigint, cd_material_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_autor_w	bigint;
qt_existe_w		bigint;	
					

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	autorizacao_cirurgia a,
	material_autor_cirurgia b
where	b.nr_seq_autorizacao	= a.nr_sequencia	
and	a.nr_seq_agenda		=  nr_seq_agenda_p
and	b.cd_material		= cd_material_p;



if (coalesce(nr_seq_agenda_p,0) <> 0) and (coalesce(qt_existe_w,0) > 0) then
	begin
	
	select 	b.nr_sequencia
	into STRICT	nr_sequencia_autor_w
	from 	material_autor_cirurgia b,
		autorizacao_cirurgia a
	where	b.nr_seq_autorizacao	= a.nr_sequencia
	and	a.nr_seq_agenda		= nr_seq_agenda_p
	and	b.cd_material		= cd_material_p;	
	
	if (coalesce(nr_sequencia_autor_w,0) > 0) then
		
		update	material_autor_cirurgia
		set 	ie_valor_conta 	= ie_opcao_p,
                nm_usuario = nm_usuario_p,
                dt_atualizacao = clock_timestamp()
		where 	nr_sequencia 	= nr_sequencia_autor_w;
	end if;

	end;
end if;	

if (coalesce(qt_existe_w,0) = 0)  then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263403);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
