CREATE OR REPLACE PROCEDURE gerar_usuario_sistema ( nm_usuario_p text, cd_setor_atendimento_p bigint, cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, nm_usuario_acao_p text, ie_gerar_cargo_setor_p text, cd_cargo_p bigint, ds_login_p text, cd_barras_p text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Gerar um usuário no sistema a partit do cadastro do funcionário.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nm_pessoa_fisica_w		varchar(255);
ie_tipo_evolucao_w			varchar(3);
nr_seq_classif_w			bigint;
nr_seq_pep_perf_usu_w		bigint;
ie_senha_w			varchar(255);
ds_senha_w			varchar(255);


BEGIN
ie_senha_w := obter_param_usuario(230, 25, obter_perfil_ativo, nm_usuario_acao_p, cd_estabelecimento_p, ie_senha_w);

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then
	select	substr(obter_nome_pf(cd_pessoa_fisica_p),1,255)
	into STRICT	nm_pessoa_fisica_w
	;

	/* afstringari 161968 */

	select	max(ie_tipo_evolucao),
		max(nr_seq_classif),
		max(nr_seq_pep_perf_usu) /* afstringari 161967 */
	into STRICT	ie_tipo_evolucao_w,
		nr_seq_classif_w,
		nr_seq_pep_perf_usu_w
	from	cargo
	where	cd_cargo	= cd_cargo_p;

	if (coalesce(ie_senha_w,'U') = 'M') then
		select	max(a.cd_funcionario)
		into STRICT	ds_senha_w
		from	pessoa_fisica a
		where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;

		if (coalesce(ds_senha_w,'X') = 'X') then
			--Não foi possível criar o usuário. Senha inválida');
			CALL Wheb_mensagem_pck.exibir_mensagem_abort(261638);
		end if;

	elsif (coalesce(ie_senha_w,'U') = 'N') then
		ds_senha_w	:= '123456';
	else
		ds_senha_w	:= nm_usuario_p;
	end if;

	insert into usuario(nm_usuario, ds_usuario, ds_senha,
		ie_situacao, dt_atualizacao, nm_usuario_atual,
		cd_pessoa_fisica, cd_setor_atendimento, cd_estabelecimento,
		qt_dia_senha, ie_anexar_arquivo, ie_comunic_interna,
		ie_versao_anterior, ie_evento_agenda, ie_evento_sac,
		ie_evento_processo, ie_evento_prescr, ie_evento_proc_agenda,
		ie_evento_comunic, ie_evento_alerta, ie_evento_aprov_compra,
		ie_evento_exame_urg, ie_evento_aprov_doc, ie_fechar_tasymon,
		ie_evento_lib_telefone, nm_usuario_orig, ie_mostrar_anexo_comunic,
		ie_evento_ordem_serv, ie_evento_obj_inv, ie_evento_recoleta,
		ie_chamar_tasymon, ie_atualizar_dic,IE_EVENTO_SUSP_EXAME,
		ie_tipo_evolucao, nr_seq_classif, nr_seq_perfil, dt_atualizacao_nrec, nm_usuario_nrec,
		cd_barras, ds_login, ie_can_view_patient_info, ie_can_view_sensitive_info)
	values (nm_usuario_p, nm_pessoa_fisica_w, ds_senha_w,
		'A', clock_timestamp(), nm_usuario_p,
		cd_pessoa_fisica_p, cd_setor_atendimento_p, cd_estabelecimento_p,
		180, 'N', 'S',
		'N', 'N', 'N',
		'N', 'N', 'N',
		'N', 'N', 'N',
		'N', 'N', 'S',
		'N', nm_usuario_acao_p, 'N',
		'N', 'N', 'N',
		'N', 'N','N',
		ie_tipo_evolucao_w,
		nr_seq_classif_w,
		nr_seq_pep_perf_usu_w,clock_timestamp(), nm_usuario_acao_p,
		cd_barras_p, ds_login_p,'Y','Y');

	CALL enviar_comunic_regra_func_usu(	'NU',
					nm_usuario_p,
					nm_usuario_acao_p,
					cd_pessoa_fisica_p,
					'',
					'');

	if (coalesce(ie_gerar_cargo_setor_p,'N') = 'S') then
		CALL gerar_cargo_perfil_setor(	cd_cargo_p,
						cd_pessoa_fisica_p,
						'',
						cd_setor_atendimento_p,
						nm_usuario_acao_p,
						nm_usuario_p,
						cd_estabelecimento_p);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
