CREATE OR REPLACE PROCEDURE gerar_usuario_terceiro ( NR_SEQ_TERCEIRO_P bigint, CD_ESTABELECIMENTO_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


CD_PESSOA_FISICA_W		varchar(10);
nm_usuario_terc_w		varchar(255);

c01 CURSOR FOR

        SELECT	a.cd_pessoa_fisica
        from	terceiro_pessoa_consulta a
        where	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
        and	a.nr_seq_terceiro	= nr_seq_terceiro_p;


BEGIN

open	c01;
loop
fetch	c01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	insert	into usuario_terceiro(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_fisica,
		cd_estabelecimento,
		nr_seq_terceiro)
	values (nextval('usuario_terceiro_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica_w,
		cd_estabelecimento_p,
		nr_seq_terceiro_p);

end	loop;
close	c01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
