CREATE OR REPLACE PROCEDURE gerar_vaga_agenda_exame ( nr_seq_agenda_p bigint, nr_seq_interno_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
				 
				 
ds_erro_w		varchar(255);
qt_existe_vaga_w		bigint;

BEGIN
 
if (nr_seq_interno_p IS NOT NULL AND nr_seq_interno_p::text <> '') then 
	begin 
 
	update	agenda_paciente 
	set	ie_necessita_internacao	=	'S', 
		nm_usuario	= nm_usuario_p 
	where	nr_sequencia	= nr_seq_agenda_p;
	 
	ds_erro_w := gerar_dados_agenda_exame(nr_seq_agenda_p, nr_seq_interno_p, nm_usuario_p, cd_estabelecimento_p, ds_erro_w);
	 
	select	count(*) 
	into STRICT	qt_existe_vaga_w 
	from	gestao_vaga 
	where	nr_seq_agenda = nr_seq_agenda_p;
	 
	if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') and (qt_existe_vaga_w > 0)then 
		begin 
		ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(281293,null);
		end;
	end if;
	end;
end if;
if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then 
	begin 
	ds_erro_p	:=	ds_erro_w;
	end;
end if;
 
commit;
 
end;
$body$
