CREATE OR REPLACE PROCEDURE gerar_valor_item_req ( nr_requisicao_p bigint, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_item_w			double precision;
ie_valor_gerar_item_req_w	varchar(1);
cd_estabelecimento_w	smallint;


BEGIN
select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;

ie_valor_gerar_item_req_w	:= coalesce(obter_valor_param_usuario(919, 82, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w),'U');

if (ie_valor_gerar_item_req_w <> 'N') then
   begin
   if (ie_valor_gerar_item_req_w = 'U') then
      vl_item_w := coalesce(sup_obter_valores_material_req(nr_requisicao_p, nr_seq_item_p,'U'),0);
   elsif (ie_valor_gerar_item_req_w = 'C') then
      vl_item_w := coalesce(sup_obter_valores_material_req(nr_requisicao_p, nr_seq_item_p,'C'),0);
	end if;

   update	item_requisicao_material
	set	vl_unit_previsto = vl_item_w
	where	nr_requisicao = nr_requisicao_p
	and	nr_sequencia = nr_seq_item_p;
   end;
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
