SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_valor_liquido_extrato (nr_seq_movto_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_liquido_res_w	double precision;
vl_bruto_res_w		double precision;
nr_seq_extrato_res_w	bigint;
qt_nulo_w		bigint;

vl_total_liquido_w	double precision;


BEGIN

select	max(a.nr_seq_extrato_res)
into STRICT	nr_seq_extrato_res_w
from	extrato_cartao_cr_movto a
where	a.nr_sequencia		= nr_seq_movto_p;

select	count(*)
into STRICT	qt_nulo_w
from	extrato_cartao_cr_movto a
where	coalesce(a.vl_liquido::text, '') = ''
and	a.nr_seq_extrato_res	= nr_seq_extrato_res_w;

select	coalesce(max(a.vl_bruto),0),
	coalesce(max(a.vl_liquido),0)
into STRICT	vl_bruto_res_w,
	vl_liquido_res_w
from	extrato_cartao_cr_res a
where	a.nr_sequencia		= nr_seq_extrato_res_w;

/* valor líquido proporcional */

update	extrato_cartao_cr_movto
set	vl_liquido		= (coalesce(vl_parcela,0) * vl_liquido_res_w) / vl_bruto_res_w
where	nr_sequencia		= nr_seq_movto_p;

if (coalesce(qt_nulo_w,0)	= 1) then	/* ajustar a sobra do valor líquido do resumo */
	select	sum(coalesce(a.vl_liquido,(coalesce(a.vl_parcela,0) * vl_liquido_res_w) / vl_bruto_res_w))
	into STRICT	vl_total_liquido_w
	from	extrato_cartao_cr_movto a
	where	a.nr_seq_extrato_res	= nr_seq_extrato_res_w;

	update	extrato_cartao_cr_movto
	set	vl_liquido	= coalesce(vl_liquido,0) + vl_liquido_res_w - vl_total_liquido_w
	where	nr_sequencia	= nr_seq_movto_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
