SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_vencimentos_orc (nr_sequencia_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_gerar_vencimento_p text) AS $body$
DECLARE


qt_reg_venc_w			integer;
cd_condicao_pagamento_w		bigint;
vl_desconto_w			double precision;
vl_desconto_proced_w		double precision;
vl_desconto_material_w		double precision;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_gerar_vencimento_p = 'S') then

		delete  from orcamento_paciente_venc
		where  nr_sequencia_orcamento = nr_sequencia_p;

	end if;


	select 	count(*)
	into STRICT	qt_reg_venc_w
	from 	orcamento_paciente_venc
	where 	nr_sequencia_orcamento = nr_sequencia_p;




	if (qt_reg_venc_w = 0) then

		select	cd_condicao_pagamento
		into STRICT	cd_condicao_pagamento_w
		from 	orcamento_paciente
		where 	nr_sequencia_orcamento = nr_sequencia_p;

		CALL Atualizar_Vencimento_Orcamento( nr_sequencia_p,
                          			cd_estabelecimento_P,
						cd_condicao_pagamento_w,
						clock_timestamp(),
						nm_usuario_p);

	end if;

	select	coalesce(vl_desconto,0)
	into STRICT	vl_desconto_w
	from	orcamento_paciente
	where	nr_sequencia_orcamento		= nr_sequencia_p;

	if (vl_desconto_w		= 0) then
		select	coalesce(sum(vl_desconto),0)
		into STRICT	vl_desconto_proced_w
		from	orcamento_paciente_proc
		where	nr_sequencia_orcamento = nr_sequencia_p;

		select	coalesce(sum(vl_desconto),0)
		into STRICT	vl_desconto_material_w
		from	orcamento_paciente_mat
		where	nr_sequencia_orcamento = nr_sequencia_p;

		vl_desconto_w		:= vl_desconto_proced_w + vl_desconto_material_w;

		update	orcamento_paciente
		set	vl_desconto	= vl_desconto_w
		where	nr_sequencia_orcamento = nr_sequencia_p;
	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
