CREATE OR REPLACE PROCEDURE gerar_vencimento_contrato ( nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_existe_w		bigint;
qt_meses_w		bigint;
dt_inicio_w		timestamp;
dt_fim_w			timestamp;
dt_vencimento_w		timestamp;
qt_existe_contrato_w	bigint;

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	contrato_venc
where	nr_seq_contrato = nr_seq_contrato_p;

select	count(*)
into STRICT	qt_existe_contrato_w
from	contrato
where	nr_sequencia = nr_seq_contrato_p;

if (qt_existe_contrato_w > 0) and (qt_existe_w = 0) then
	select	dt_inicio,
		coalesce(dt_fim, PKG_DATE_UTILS.start_of(fim_ano(clock_timestamp()),'dd',0))
	into STRICT	dt_inicio_w,
		dt_fim_w
	from	contrato
	where	nr_sequencia = nr_seq_contrato_p;

	select	trunc(dividir(obter_dias_entre_datas(dt_inicio_w, dt_fim_w),30))
	into STRICT	qt_meses_w
	;

	for i in 0..qt_meses_w + 1 loop
		begin
		dt_vencimento_w	:= PKG_DATE_UTILS.ADD_MONTH(dt_inicio_w, i, 0);

		if (PKG_DATE_UTILS.start_of(dt_vencimento_w,'dd',0) >= PKG_DATE_UTILS.start_of(dt_inicio_w,'dd',0) and
			PKG_DATE_UTILS.start_of(dt_vencimento_w,'dd',0) <= PKG_DATE_UTILS.start_of(dt_fim_w,'dd',0)) then
			begin
			insert into contrato_venc(
					nr_sequencia,
					nr_seq_contrato,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_vencimento,
					nr_seq_nota)
				values (	nextval('contrato_venc_seq'),
					nr_seq_contrato_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					dt_vencimento_w,
					null);
			end;
		end if;
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
