SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_versao_alteracao ( nm_tabela_p text, ie_acao_p text, cd_registro_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_status_w MED_PROTOCOLO_EXAME_HIST.IE_STATUS%type;
nr_versao_w MED_PROTOCOLO_EXAME_HIST.NR_VERSAO%type;

/*

ie_acao_p
L = Liberar
D = Desfazer liberacao

*/
BEGIN

if (ie_acao_p = 'D') then
	ie_status_w := 'PA';
elsif (ie_acao_p = 'L') then
	ie_status_w := 'AP';
end if;

if (NM_TABELA_P = 'MED_PROTOCOLO_EXAME') THEN

	select coalesce(max(nr_versao),0)
	into STRICT nr_versao_w
	from MED_PROTOCOLO_EXAME_HIST
	where NR_SEQ_PROT_EXAME = cd_registro_p;

	insert into MED_PROTOCOLO_EXAME_HIST(
		NR_SEQUENCIA,
		NR_VERSAO,
		NR_SEQ_PROT_EXAME,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		IE_STATUS
	) values (
		nextval('med_protocolo_exame_hist_seq'),
		CASE WHEN ie_acao_p='D' THEN  nr_versao_w+1  ELSE nr_versao_w END ,
		cd_registro_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_status_w
	);

elsif (NM_TABELA_P = 'MED_PROTOCOLO_RECEITA') THEN

	select coalesce(max(nr_versao),0)
	into STRICT nr_versao_w
	from MED_PROTOCOLO_RECEITA_HIST
	where NR_SEQ_PROT_RECEITA = cd_registro_p;

	insert into MED_PROTOCOLO_RECEITA_HIST(
		NR_SEQUENCIA,
		NR_VERSAO,
		NR_SEQ_PROT_RECEITA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		IE_STATUS
	) values (
		nextval('med_protocolo_receita_hist_seq'),
		CASE WHEN ie_acao_p='D' THEN  nr_versao_w+1  ELSE nr_versao_w END ,
		cd_registro_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_status_w
	);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
