CREATE OR REPLACE PROCEDURE gerar_viagem_os (cd_pessoa_fisica_p text, dt_saida_prev_p timestamp, dt_retorno_prev_p timestamp, nr_seq_ordem_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_viagem_p INOUT bigint) AS $body$
DECLARE


