	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_pessoa_fisica_p,
	dt_saida_prev_p,
	dt_retorno_prev_p,
	'0');

insert into via_os_viagem(nr_sequencia,
	NR_SEQ_ORDEM_SERV,
	DT_ATUALIZACAO,
	NM_USUARIO,
	DT_ATUALIZACAO_NREC,
	NM_USUARIO_NREC,
	NR_SEQ_VIAGEM)
values (nextval('via_os_viagem_seq'),
	nr_seq_ordem_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_viagem_w);

