SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_via_tit_rec_bloqueto ( nr_titulo_p bigint, ie_origem_via_p text, nm_usuario_p text) AS $body$
DECLARE


cd_tipo_juros_w		titulo_receber.cd_tipo_taxa_juro%type;
cd_tipo_multa_w		titulo_receber.cd_tipo_taxa_multa%type;
tx_juros_w		titulo_receber.tx_juros%type;
tx_multa_w		titulo_receber.tx_multa%type;
vl_juros_w		titulo_receber_bloqueto.vl_juros%type;
vl_multa_w		titulo_receber_bloqueto.vl_multa%type;
vl_titulo_w		titulo_receber.vl_titulo%type;
nr_nosso_numero_w	titulo_receber.nr_nosso_numero%type;
nr_bloqueto_w		titulo_receber.nr_bloqueto%type;
dt_vencimento_w		titulo_receber.dt_pagamento_previsto%type;
nr_seq_conta_banco_w	titulo_receber.nr_seq_conta_banco%type;
cd_convenio_banco_w	banco_estabelecimento.cd_convenio_banco%type;
cd_conta_w		banco_estabelecimento.cd_conta%type;
ie_digito_conta_w	banco_estabelecimento.ie_digito_conta%type;
cd_agencia_w		agencia_bancaria.cd_agencia_bancaria%type;
ie_digito_agencia_w	agencia_bancaria.ie_digito%type;
cd_banco_w		banco_estabelecimento.cd_banco%type;
nr_via_boleto_w		titulo_receber_bloqueto.nr_via_boleto%type;


BEGIN

select	cd_tipo_taxa_juro,
	cd_tipo_taxa_multa,
	tx_juros,
	tx_multa,
	vl_titulo,
	nr_nosso_numero,
	nr_bloqueto,
	dt_pagamento_previsto,
	nr_seq_conta_banco,
	substr(obter_juros_multa_titulo(nr_titulo, clock_timestamp(), 'R', 'J'),1,20) vl_juros,
	substr(obter_juros_multa_titulo(nr_titulo, clock_timestamp(), 'R', 'M'),1,20) vl_multa
into STRICT	cd_tipo_juros_w,
	cd_tipo_multa_w,
	tx_juros_w,
	tx_multa_w,
	vl_titulo_w,
	nr_nosso_numero_w,
	nr_bloqueto_w,
	dt_vencimento_w,
	nr_seq_conta_banco_w,
	vl_juros_w,
	vl_multa_w
from	titulo_receber
where	nr_titulo	= nr_titulo_p;

select	a.cd_banco,
	substr(a.cd_conta,1,15),
	a.ie_digito_conta,
	a.cd_convenio_banco,
	b.cd_agencia_bancaria,
	b.ie_digito
into STRICT	cd_banco_w,
	cd_conta_w,
	ie_digito_conta_w,
	cd_convenio_banco_w,
	cd_agencia_w,
	ie_digito_agencia_w
from	banco_estabelecimento a,
	agencia_bancaria b
where	a.cd_agencia_bancaria	= b.cd_agencia_bancaria
and	a.cd_banco		= b.cd_banco
and	nr_sequencia		= nr_seq_conta_banco_w;

select	coalesce(max(nr_via_boleto),0) + 1
into STRICT	nr_via_boleto_w
from	titulo_receber_bloqueto
where	nr_titulo = nr_titulo_p;
	

insert into titulo_receber_bloqueto(
	nr_sequencia,
	nr_via_boleto,
	cd_banco,
	cd_agencia_bancaria,
	ie_digito_agencia,
	cd_conta,
	ie_digito_conta,
	nr_seq_conta_banco,
	cd_convenio_banco,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	dt_pagamento_previsto,
	ie_origem_via,
	nr_bloqueto,
	nr_nosso_numero,
	nr_titulo,
	vl_titulo,
	cd_tipo_taxa_juro,
	cd_tipo_taxa_multa,
	tx_juros,
	tx_multa,
	vl_juros,
	vl_multa,
	ds_stack)
values (nextval('titulo_receber_bloqueto_seq'),
	nr_via_boleto_w,
	cd_banco_w,
	cd_agencia_w,
	ie_digito_agencia_w,
	cd_conta_w,
	ie_digito_conta_w,
	nr_seq_conta_banco_w,
	cd_convenio_banco_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	dt_vencimento_w,
	ie_origem_via_p,
	nr_bloqueto_w,
	nr_nosso_numero_w,
	nr_titulo_p,
	vl_titulo_w,
	cd_tipo_juros_w,
	cd_tipo_multa_w,
	tx_juros_w,
	tx_multa_w,
	vl_juros_w,
	vl_multa_w,
	substr('CallStack Insert: ' || substr(dbms_utility.format_call_stack,1,3980),1,4000));

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
